INSERT INTO `mz_document` (`id`, `uid`, `title`, `category_id`, `display`, `status`, `views`, `create_time`, `update_time`, `from`, `level`, `description`, `order_num`, `content`) VALUES
(1, 1, '人力资源服务中心的职能', 1, 1, 0, 0, 1412583097, 0, NULL, 31, NULL, 1, '<p> 负责辖区内人力资源开发利用、城乡劳动力就业、流动就业、涉外就业的具体服务工作；负责辖区内政策性就业安置、失业职工再就业的服务工作；负责劳务扶贫和劳务合作工作；协助承办用工申报工作；负责流动就业统计工作；负责使用流动人员调配费核缴工作；负责辖区内职业技能培训和职业技能考核鉴定服务工作；负责人才供求信息收集、贮存，为用人单位推荐专业技术、管理人员；为专业技术、管理人员提供需求信息，推荐就业；提供人才流动有关政策、法规咨询服务；举办本辖区劳动力和人才集市、招聘会；承办有关科技及专业技术资格（职称）的报批工作；开展智力成果交流、转让和推广应用。 </p>'),
(2, 1, '负责辖区内人力资源开发利用', 1, 1, 0, 0, 1412583097, 0, NULL, 31, NULL, 1, '<p> 东莞市职业介绍服务中心是我市唯一面向社会的公益性职业介绍机构。主要承担政府各项公益性的公共就业服务，包括： 就业政策法规咨询；职业供求信息、市场工资指导价位信息和职业培训信息发布；就业失业登记服务；职业指导、职业介绍服务；就业困难人员技能训练、岗位安置、补贴发放等就业援助服务；自主创业项目开发、开业指导、小额贷款、税费减免等扶持服务；跨地区劳务交流服务，以及其他公共就业服务。 </p>'),
(3, 1, '东莞市职业介绍服务中心是我市唯一面向社会的公益性职业介绍机构。主要承担政府各项公益性的公共就业服务', 1, 1, 0, 0, 1412583098, 0, NULL, 31, NULL, 1, ''),
(4, 1, '（一）贯彻落实党和政府促进就业的方针政策；负责国家、省、市促进就业再就业各项政策措施的具体组织、实施工作。', 1, 1, 0, 0, 1412583098, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;一、主要职能 &nbsp; &nbsp;1、就业和失业登记管理 &nbsp; &nbsp;2、就业困难人员认定 &nbsp; &nbsp;3、就业困难人员公益性岗位和就业岗位援助补贴 &nbsp; &nbsp;4、就业困难人员社会保险补贴 &nbsp; &nbsp;5、就业集结号发布用工信息 &nbsp; &nbsp;6、求职登记及职业介绍 &nbsp; &nbsp;7、职业介绍机构审批和年审 &nbsp; &nbsp;8、用工登记备案管理 &nbsp; &nbsp;9、劳动保障事务代理 &nbsp; &nbsp;10、单纯档案托管 </p>'),
(5, 1, '贯彻执行国家和省有关劳动工作的方针政策', 1, 1, 0, 0, 1412583098, 0, NULL, 31, NULL, 1, '<p> 贯彻执行国家和省有关劳动工作的方针政策、法律法规，草拟劳动工作地方性法规、规章、政策；编制劳动事业发展中、长期规划和年度计划，并组织实施；执行和补充制定劳动规范基本标准、管理规则，并组织实施和监督检查 </p>'),
(6, 1, '执行国家和省有关劳动就业的基本政策', 1, 1, 0, 0, 1412583098, 0, NULL, 31, NULL, 1, '<p> 执行国家和省有关劳动就业的基本政策，制定促进城乡就业的工作措施并组织实施；规划和指导劳动力市场发展，综合管理各类职业介绍中介机构和涉外就业服务机构；负责外省市在莞设立劳务办事处的审批、管理；组织建立、健全就业服务体系；统筹管理城乡劳动力就业、流动就业、涉外就业；指导并监督实施政策性就业安置和调配工作。 </p>'),
(7, 1, '执行国家和省制定的职业分类', 1, 1, 0, 0, 1412583098, 0, NULL, 31, NULL, 1, '<p> 执行国家和省制定的职业分类、职业技能标准和行业标准，拟定职业技能培训和就业培训的规划、政策和工作措施，并组织实施；规划指导和组织建立、健全职业技能培训服务体系，综合管理社会力量举办的各类非学历职业技能培训服务机构；实施劳动预备制度和实施职业资格证书制度，综合管理职业技能考核鉴定机构；指导和综合管理市技工学校。 </p>'),
(8, 1, '综合管理', 1, 1, 0, 0, 1412583099, 0, NULL, 31, NULL, 1, '<p> 综合管理各类企业劳动关系协调工作，制定各类企业调整劳动关系的政策和工作措施；负责劳动合同鉴证、劳动争议处理、劳动争议仲裁工作。 </p>'),
(9, 1, '执行国家和省有关企业职工工资宏观政策', 1, 1, 0, 0, 1412583099, 0, NULL, 31, NULL, 1, '<p> 执行国家和省有关企业职工工资宏观政策，制定具体工作措施，并组织实施；拟定企业工资指导线、最低工资标准和工资收入调节的有关政策，协调企业人员工资报酬分配关系；拟定国有企业经营者收入分配政策，审核国有企业的工资总额；指导实施有关职工福利政策，依法监督企业职工工作时间、休息休假制度和女工、未成年工的特殊劳动保护政策的实施。 </p>'),
(10, 1, '执行宏观政策 ', 1, 1, 0, 0, 1412583099, 0, NULL, 31, NULL, 1, '<p> 执行国家和省有关企业职工工资宏观政策 </p>'),
(11, 1, '贯彻落实', 1, 1, 0, 0, 1412583100, 0, NULL, 31, NULL, 1, '<p> 贯彻落实党和政府促进就业的方针政策；负责国家、省、市促进就业再就业各项政策措施的具体组织、实施工作。&nbsp; </p>'),
(12, 1, '指导和协调', 1, 1, 0, 0, 1412583100, 0, NULL, 31, NULL, 1, '<p> 指导和协调全市各镇街人力资源服务机构、各村（社区）开展公共就业服务各项工作。 </p>'),
(13, 1, ' 负责人力', 1, 1, 0, 0, 1412583100, 0, NULL, 31, NULL, 1, '<p> 负责人力资源开发利用的各项具体工作；负责城乡劳动力就业和再就业的具体管理工作，包括就业失业登记、再就业管理、政策性就业安置、流动就业管理等。 </p>'),
(14, 1, '资源调配', 1, 1, 0, 0, 1412583100, 0, NULL, 31, NULL, 1, '<p> 负责人力资源市场信息网络建设和管理；负责劳动力资源库及相关系统建设、管理及维护；负责就业调查、统计、分析、发布及发展预测工作。 </p>'),
(15, 1, '资金的管理', 1, 1, 0, 0, 1412583101, 0, NULL, 31, NULL, 1, '<p> 负责促进就业各项资金的管理，包括各项就业补贴、转业训练、劳动力市场建设、公共就业服务机构建设等促进就业资金的管理；负责镇街人力资源服务机构经费管理的指导、协调和财务监督；协助市财政部门开展有关就业调配费核缴工作。 </p>'),
(16, 1, '负责就业服务', 1, 1, 0, 0, 1412583101, 0, NULL, 31, NULL, 1, '<p> 负责就业服务领域对外交流与合作项目的具体实施工作；负责落实省、市有关劳务扶贫和劳务协作工作；负责外省市驻莞劳务办事机构的管理工作。 </p>'),
(17, 1, '推广应用', 1, 1, 0, 0, 1412583101, 0, NULL, 31, NULL, 1, '<p> 负责就业服务领域科学技术研究和成果推广应用，以及宣传教育工作。 </p>'),
(18, 1, '公共服务', 1, 1, 0, 0, 1412583101, 0, NULL, 31, NULL, 1, '<p> 公共就业服务登记的对象是本市户籍劳动年龄内具有劳动能力和就业愿望的劳动力。 服务对象到服务机构填写《东莞市公共就业服务登记表》（一式两份），并提交个人身份证复印件。服务机构将服务对象的相关信息录入劳动力资源信息库，并建立个人服务档案。 服务对象一经登记，即获得公共就业服务登记号。服务对象可凭公共就业服务登记号享受本市各项公共就业服务及各项就业优惠政策 </p>'),
(19, 1, '就业服务', 1, 1, 0, 0, 1412583102, 0, NULL, 31, NULL, 1, '<p> 规范公共就业服务以服务对象登记为起点，以成功实现就业创业为终点，包括基本服务、职业介绍、职业素质测评、职业技能培训、个人特向职业指导、动态跟踪等六个服务项目。服务机构的服务人员可以根据服务对象的实际情况为其制订个性化的就业服务流程；服务对象也可以根据个人意愿自主选定相应的服务项目。 </p>'),
(20, 1, '基本服务', 1, 1, 0, 0, 1412583102, 0, NULL, 31, NULL, 1, '<p> 公共就业服务登记。公共就业服务登记是指服务机构为服务对象建立个人档案，并将其个人相关信息录入劳动力资源信息库。 </p>'),
(21, 1, '基础评估', 1, 1, 0, 0, 1412583102, 0, NULL, 31, NULL, 1, '<p> 通过面谈与交流，了解服务对象的个人心理、家庭状况、职业倾向等情况，并结合服务对象的年龄、学历、技术专长、工作经历等情况对其进行初步评估。根据评估结果为服务对象制订个性化的就业服务流程。 </p>'),
(22, 1, '基本职业指导', 1, 1, 0, 0, 1412583102, 0, NULL, 31, NULL, 1, '<p> 根据基础评估结果，指引服务对象选择合适的服务项目，并就相应服务项目详细讲解有关就业服务的政策，提供相应的择业基本指导。 </p>'),
(23, 1, '职业介绍', 1, 1, 0, 0, 1412583103, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;职业介绍服务是指经过基本服务后，把有需要的服务对象根据评估情况进行分类，开展有针对性的职业介绍服务。服务内容包括： &nbsp; &nbsp;1、推荐就业。由服务机构的工作人员就当时有效的岗位信息，进行岗位匹配，推荐服务对象到企业面试。 &nbsp; &nbsp;2、定期召开定向型招聘会。服务机构针对大多数服务对象的就业意向，定期、分类召开中小型的招聘会，为不同类别的服务对象提供就业信息。 &nbsp; &nbsp;3、特殊岗位推荐。对于有特殊技能的服务对象，由服务机构工作人员选取合适的岗位，进行单个推荐服务。 </p>'),
(24, 1, '职业素质测评', 1, 1, 0, 0, 1412583103, 0, NULL, 31, NULL, 1, '<p> &nbsp; 职业素质测评服务是指经过基础评估后，对有需要的服务对象利用职业素质测评系统进行职业素质测评。 &nbsp; 职业素质测评系统是一套由30种职业测验组成，专门用于各类职业人员素质论断、选拔和预测的大型职业素质测评工具。该系统主 &nbsp; &nbsp;要是对个人的能力、心理特质、职业倾向和就业动力等各方面的情况进行测评及分析，得出个人素质各方面指标参数，指导服务对象 &nbsp;根据个人特点重新确定求职方向，并制订合适的求职计划。 &nbsp; 通过该项服务，使服务对象能够明确适合自己的工作岗位，由原来被动地等待介绍工作，转变为主动地制定个人求职计划，并利用各种信息去寻找工作岗位，更快地实现就业创业。 </p>'),
(25, 1, '职业技能培训', 1, 1, 0, 0, 1412583103, 0, NULL, 31, NULL, 1, '<p> 职业技能培训服务是指经过基础评估后，对有需要的服务对象的就业技能进行重新评估，建议其参加合适的技能培训，并对其进行培训情况跟踪以及培训后就业情况跟踪。 </p>'),
(26, 1, '指导思想、总体目标和范围对象', 2, 1, 0, 0, 1412583103, 0, NULL, 31, NULL, 1, '<p> 以邓小平理论和“三个代表”重要思想为指导，深入贯彻落实科学发展观，坚持党管人才原则，坚持人才优先、高端引领，紧紧围绕我市产业结构转型升级的需要，努力培养和引进一批我市经济社会发展需要的高层次人才，为我市加快转型升级，建设幸福东莞，提供人才保障和智力支持 </p>'),
(27, 1, '总体目标', 2, 1, 0, 0, 1412583104, 0, NULL, 31, NULL, 1, '<p> 全面创新高层次人才培养、吸引、使用、保障、激励等各项措施，通过从海内外大力引进和本土自主培养等途径，用5－10年时间，集聚一批达到国际先进水平的科技创新团队和领军人才、一批专业地位突出的创新创业领军人才、一批在专业技术技能方面发展潜力巨大的后备培养人才，努力实现人才结构明显改善，人才素质明显提高，人才效能明显增强，人才环境明显优化，人才发展体制机制明显创新。 </p>'),
(28, 1, '广东省外国专家来华工作许可办理规定', 2, 1, 0, 0, 1412583104, 0, NULL, 31, NULL, 1, '<p> 外国专家来华工作许可是由国务院针对外国专家来华工作准入管理制定的行政许可事项（国务第412号令）（省政府令106号第一点第108项为“外国专家来粤工作许可）。符合条件的外国人通过申请并获得批准后，可以取得《外国专家来华工作许可证》并办理职业（Z）签证、《外国专家证》以及在华居留许可。 一、申请资格 应聘来粤的外国专家，一般分为经济技术类专家和文教类专家。前者指应聘在广东省各级政府机关、经济和社会管理部门、科研机构、工商企业等领域的外籍专业技术人员，后者指应聘在广东省各类学校及其它教育机构、新闻出版、医药卫生、文化艺术、体育等部门工作的外籍专业人员。申请在粤工作的外国专家应具备以下条件： （一）遵守中国法律法规、身体健康、无违法犯罪记录、无不良行为； （二）必须是聘用单位工作需要； （三）具有大学学士以上学位和5年以上相关工作经历。文教类的语言教师应具有大学学士以上学位和2年以上相关工作经历，且教授的语言应是其母语。 二、 办理程序 （一）申办《外国专家来华工作许可证》 1、外国专家在入境前需由聘用单位向省外专局或已委托相关业务的地级市外专局（广州、深圳）申请办理《外国专家来华工作许可证》（可派人直接报送材料或通过邮寄方式递送），向省外办（或广州、深圳市外办）申请办理《被授权单位的签证通知函（电）》（可由外专局转送）。 2、对申请材料不齐全或者不符合规定的，省外国专家局自收到申请材料之日起五个工作日内电话或书面告知申请人需要补正的全部内容。经告知仍无法补正的，不予受理，省外国专家局将出具加盖印章的《行政许可不予受理通知书》，并注明不予受理的理由。 3、省外国专家局依据有关规定，对受理的申请材料进行审查，并在规定期限（二十个工作日）内做出决定。对给予行政许可的决定，由省外国专家局颁发、送达由国家外国专家局统一印制的并加盖本行政机关印章且由本行政机关授权人在相应栏目上签字的《外国专家来华工作许可证》。对不予行政许可的决定，由省外国专家局出具加盖印章的《不予行政许可通知书》，并注明不予许可的理由。 （二）办理职业（Z）签证 聘请单位将《外国专家来华工作许可证》原件、被授权单位的签证通知函（电）寄给拟聘人员（聘用单位应保留许可证复印件及许可证存根），由其本人凭《外国专家来华工作许可证》原件、被授权单位的签证通知函（电）及有效护照，向中国驻外使领馆申请办理职业（Z）签证，中国驻外使领馆留存工作许可原件。 （三）申办《外国专家证》 1、已办理了《外国专家来华工作许可证》并持职业（Z）签证来粤工作的外国专家，在入境后十五日内，由聘用单位持《外国专家来华工作许可证》存根、护照复印件（含签证页）及健康证明等材料到省外国专家局或省外国专家局已委托办理相关业务的工作所在地的地级以上市（广州、深圳、佛山、江门、珠海）外国专家管理部门申办《外国专家证》。对材料不齐全的，外国专家管理部门自收到申请材料之日起五个工作日内电话或书面告知申请人需要补正的全部内容；材料齐全的，外国专家管理部门在五个工作日内发给《外国专家证》。 2、已在我境内任教且持有职业（Z）签证及《外国专家证》的外国专家，转换工作单位时需交回原有《外国专家证》，并由新的聘请单位为其重新申办《外国专家证》（审批程序和时限与办理《外国专家来华工作许可证》相同）。 （四）办理外国人居留许可手续 外国专家在入境后三十日内凭职业（Z）签证、健康证明和《外国专家证》到所在地公安出入境管理部门办理外国人居留许可手续。 （五）《外国专家证》的延期 合同期满后又续约或合同未到期但《外国专家证》已到期的外国专家，须由聘用单位及时向原发证机关申请办理《外国专家证》延期手续，如提交材料齐全的，发证机关应在十五个工作日内办结。 （六）《外国专家证》的管理 1、外国专家因聘期结束或其它原因离开聘用单位，该《外国专家证》将失效，由聘用单位收回《外国专家证》，并上交发证单位注销； 2、如不慎遗失《外国专家证》，应及时向发证机关报告，并提出补发《外国专家证》的书面申请，由发证机关给予补发。 三、申报材料 （一）办理《外国专家来华工作许可证》所需材料： 1、加盖聘请单位公章的《外国专家（外籍教师）来华工作申请表》（可在http: //guangdong.caiep.org下载）； 2、个人护照复印件； 3、最高学历证书复印件或专业资格证明材料复印件； 4、个人简历（包括学历、工作经历）； 5、经中国驻外使、领馆认证的外国卫生医疗机构签发的或中国政府指定的卫生检疫部门出具的健康证明书（如只能提供私人医生出具的健康证明，则在入境后需补交由我卫生检疫部门出具的健康证明书）； 6、国家外国专家局印发的标准聘用合同（文教类）及附件，或聘用单位制定的包含标准合同所有内容的聘用合同（中英文），合同中应有医疗、意外保险条款； 7、如有随行家属，除需提供其护照复印件外。配偶须提供婚姻关系证明，18岁以下子女须提供出生证明（18岁以上子女不再作为随行家属）。 （二）已获《外国专家来华工作许可证》并持职业（Z）签证的外国专家办理《外国专家证》所需材料： 1、加盖聘请单位公章的《外国专家证申请表》（可在http: //guangdong.caiep.org下载）； 2、《外国专家来华工作许可证》复印件，申请人存根； 3、护照复印件（含签证页） 4、专家近期大1寸免冠彩色照片2张； 5、我卫生检疫部门出具的健康证明书（如在办理《外国专家来华工作许可证》时已提交了规定的健康证明，则此项可不再提供）； 6、如有随行家属，需提供近期小1寸免冠彩色照片2张。 （三）办理《外国专家证》延期所需材料： 1、加盖单位公章的申请公函； 2、新的聘用合同； 3、《外国专家证》原件。 （四）国内转聘者申请办理《外国专家证》所需材料： 1、《外国专家证申请表》（可在http: //guangdong.caiep.org下载）； 2、原《外国专家证复印件》； 3、个人简历（包括学历、工作经历） 4、上一个聘用单位（半年内）的推荐信材料复印件； 5、护照复印件（含签证、居留许可页）； 6、最高学历证书复印件或专业资格证明材料复印件； 7、如从外省转聘，需提交最近一次由其原工作省份卫生检疫部门出具的健康证明书或我省检疫部门出具的健康证明书； 8、国家外国专家局印发的标准聘用合同（文教类）及附件，或聘用单位制定的包含标准合同所有内容的聘用合同（中英文）。 9、专家近期大1寸免冠彩色照片2张； 10、如有随行家属，除需提供其护照复印件外，配偶须提供婚姻关系证明，18岁以下子女须提供出生证明（18岁以上子女不再作为随行家属）。 四、注意事项 （一）申请人虽然为外国专家，但应由聘用单位代向实施机关提出申请和办理手续，无特殊情况不得由外国专家本人前往办理； （二）各聘请单位应尽量提早准备，对拟聘外籍人员进行调查了解，上报所需所有材料，并按规定流程办理各项手续，原则上不得聘请持旅游（L）等其它种类签证入境的外籍人员，更不允许在未获得工作许可的情况下，直接让外教入境； （三）聘用合同中必须有医疗（包括大病及住院）和意外保险的条款，原则上要求聘用单位为外国专家购买，若聘用双方达成协议由外国专家本人负责，在办理专家证时必须附上所购医疗及意外保险单据的复印件。 （四）如果国家有新的相关规定，以国家规定为准。 </p>'),
(29, 1, '劳动保障规定', 2, 1, 0, 0, 1412583104, 0, NULL, 31, NULL, 1, '<p> 第一条　为了实施《劳动保障监察条例》，规范劳动保障监察行为，制定本规定。 第二条　劳动保障行政部门及所属劳动保障监察机构对企业和个体工商户（以下称用人单位）遵守劳动保障法律、法规和规章（以下简称劳动保障法律）的情况进行监察，适用本规定；对职业介绍机构、职业技能培训机构和职业技能考核鉴定机构进行劳动保障监察，依照本规定执行；对国家机关、事业单位、社会团体执行劳动保障法律情况进行劳动保障监察，根据劳动保障行政部门的职责，依照本规定执行。 第三条　劳动保障监察遵循公正、公开、高效、便民的原则。 实施劳动保障行政处罚坚持以事实为依据，以法律为准绳，坚持教育与处罚相结合，接受社会监督。 第四条　劳动保障监察实行回避制度。 第五条　县级以上劳动保障行政部门设立的劳动保障监察行政机构和劳动保障行政部门依法委托实施劳动保障监察的组织（以下统称劳动保障监察机构）具体负责劳动保障监察管理工作。 </p>'),
(30, 1, '一般规定', 2, 1, 0, 0, 1412583105, 0, NULL, 31, NULL, 1, '<p> 劳动保障行政部门对用人单位及其劳动场所的日常巡视检查，应当制定年度计划和中长期规划，确定重点检查范围，并按照现场检查的规定进行。 劳动保障行政部门对用人单位按照要求报送的有关遵守劳动保障法律情况的书面材料应进行审查，并对审查中发现的问题及时予以纠正和查处。 劳动保障行政部门可以针对劳动保障法律实施中存在的重点问题集中组织专项检查活动，必要时，可以联合有关部门或组织共同进行。 劳动保障行政部门应当设立举报、投诉信箱，公开举报、投诉电话，依法查处举报和投诉反映的违反劳动保障法律的行为。 </p>'),
(31, 1, '受理与立案', 2, 1, 0, 0, 1412583105, 0, NULL, 31, NULL, 1, '<p> &nbsp;任何组织或个人对违反劳动保障法律的行为，有权向劳动保障行政部门举报。 　劳动保障行政部门对举报人反映的违反劳动保障法律的行为应当依法予以查处，并为举报人保密；对举报属实，为查处重大违反劳动保障法律的行为提供主要线索和证据的举报人，给予奖励。 　劳动者对用人单位违反劳动保障法律、侵犯其合法权益的行为，有权向劳动保障行政部门投诉。对因同一事由引起的集体投诉，投诉人可推荐代表投诉。 　投诉应当由投诉人向劳动保障行政部门递交投诉文书。书写投诉文书确有困难的，可以口头投诉，由劳动保障监察机构进行笔录，并由投诉人签字。 　投诉文书应当载明下列事项： </p>'),
(32, 1, '调查与检查', 2, 1, 0, 0, 1412583105, 0, NULL, 31, NULL, 1, '<p> 　劳动保障监察员进行调查、检查不得少于2人。劳动保障监察机构应指定其中1名为主办劳动保障监察员。 　劳动保障监察员对用人单位遵守劳动保障法律情况进行监察时，应当遵循以下规定： 进入用人单位时，应佩戴劳动保障监察执法标志，出示劳动保障监察证件，并说明身份； 就调查事项制作笔录，应由劳动保障监察员和被调查人（或其委托代理人）签名或盖章。被调查人拒不签名、盖章的，应注明拒签情况。 &nbsp;　劳动保障监察员进行调查、检查时，承担下列义务： 依法履行职责，秉公执法； 保守在履行职责过程中获知的商业秘密； 为举报人保密。 　 &nbsp;劳动保障监察员在实施劳动保障监察时，有下列情形之一的，应当回避： 本人是用人单位法定代表人或主要负责人的近亲属的； 本人或其近亲属与承办查处的案件事项有直接利害关系的； 因其他原因可能影响案件公正处理的。 　 &nbsp;当事人认为劳动保障监察员符合本规定第二十三条规定应当回避的，有权向劳动保障行政部门申请，要求其回避。当事人申请劳动保障监察员回避，应当采用书面形式。 　 &nbsp;回避决定应在收到申请之日起3个工作日内作出。作出回避决定前，承办人员不得停止对案件的调查处理。对回避申请的决定，应当告知申请人。 </p>'),
(33, 1, '案件处理', 2, 1, 0, 0, 1412583106, 0, NULL, 31, NULL, 1, '<p> 　 &nbsp;对用人单位存在的违反劳动保障法律的行为事实确凿并有法定处罚（处理）依据的，可以当场作出限期整改指令或依法当场作出行政处罚决定。 当场作出限期整改指令或行政处罚决定的，劳动保障监察员应当填写预定格式、编有号码的限期整改指令书或行政处罚决定书，当场交付当事人。 　 &nbsp;当场处以警告或罚款处罚的，应当按照下列程序进行: 口头告知当事人违法行为的基本事实、拟作出的行政处罚、依据及其依法享有的权利； &nbsp; &nbsp;听取当事人的陈述和申辩； 填写预定格式的处罚决定书； &nbsp; &nbsp;当场处罚决定书应当由劳动保障监察员签名或者盖章； 将处罚决定书当场交付当事人，由当事人签收。 劳动保障监察员应当在2日内将当场限期整改指令和行政处罚决定书存档联交所属劳动保障行政部门存档。 　 &nbsp;对不能当场作出处理的违法案件，劳动保障监察员经调查取证，应当提出初步处理建议，并填写案件处理报批表。 案件处理报批表应写明被处理单位名称、案由、违反劳动保障法律行为事实、被处理单位的陈述、处理依据、建议处理意见。 　 &nbsp;对违反劳动保障法律的行为作出行政处罚或者行政处理决定前，应当告知用人单位，听取其陈述和申辩；法律、法规规定应当依法听证的，应当告知用人单位有权依法要求举行听证；用人单位要求听证的，劳动保障行政部门应当组织听证。 　劳动保障行政部门对违反劳动保障法律的行为，根据调查、检查的结果，作出以下处理： </p>'),
(34, 1, '档案机构及其职责', 2, 1, 0, 0, 1412583106, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;国家档案行政管理部门主管全国档案事业，对全国的档案事业实行统筹规划，织协调，统一制度，监督和指导。 县级以上地方各级人民政府的档案行政管理部门主管本行政区域内的档案事业，并对本行政区域内机关、团体、企业事业单位和其他组织的档案工作实行监督和指导。 乡、民族乡、镇人民政府应当指定人员负责保管本机关的档案，并对所属单位的档案工作实行监督和指导。 &nbsp; &nbsp;机关、团体、企业事业单位和其他组织的档案机构或者档案工作人员，负责保管本单位的档案，并对所属机构的档案工作实行监督和指导。 &nbsp; &nbsp;中央和县级以上地方各级各类档案馆，是集中管理档案的文化事业机构，负责接收、收集、整理、保管和提供利用各分管范围内的档 &nbsp;案。 &nbsp; &nbsp;档案工作人员应当忠于职守，遵守纪律，具备专业知识。 在档案的收集、整理、保护和提供利用等方面成绩显著的单位或者个人，由各级人民政府给予奖励。 </p>'),
(35, 1, '档案的管理', 2, 1, 0, 0, 1412583106, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;对国家规定的应当立卷归档的材料，必须按照规定，定期向本单位档案机构或者档案工作人员移交，集中管理，任何个人不得据为己有。 &nbsp; &nbsp;国家规定不得归档的材料，禁止擅自归档。 机关、团体、企业事业单位和其他组织必须按照国家规定，定期向档案馆移交档案。 &nbsp; &nbsp;博物馆、图书馆、纪念馆等单位保存的文物、图书资料同时是档案的，可以按照法律和行政法规的规定，由上述单位自行管理。 档案馆与上述单位应当在档案的利用方面互相协作。 &nbsp; &nbsp;各级各类档案馆，机关、团体、企业事业单位和其他组织的档案机构，应当建立科学的管理制度，便于对档案的利用；配置必要的设施，确保档案的安全；采用先进技术，实现档案管理的现代化。 &nbsp; &nbsp;保密档案的管理和利用，密级的变更和解密，必须按照国家有关保密的法律和行政法规的规定办理。 鉴定档案保存价值的原则、保管期限的标准以及销毁档案的程序和办法，由国家档案行政管理部门制定。禁止擅自销毁档案。 </p>'),
(36, 1, '档案的利用和公布 ', 2, 1, 0, 0, 1412583106, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp; 国家档案馆保管的档案，一般应当自形成之日起满三十年向社会开放。经济、科学、技术、文化等类档案向社会开放的期限，可以少于三十年，涉及国家安全或者重大利益以及其他到期不宜开放的档案向社会开放的期限，可以多于三十年，具体期限由国家档案行政管理部门制订，报国务院批准施行。 档案馆应当定期公布开放档案的目录，并为档案的利用创造条件，简化手续，提供方便。中华人民共和国公民和组织持有合法证明，可以利用已经开放的档案。 &nbsp; &nbsp; 机关、团体、企业事业单位和其他组织以及公民根据经济建设、国防建设、教学科研和其他各项工作的需要，可以按照有关规定，利用档案馆未开放的档案以及有关机关、团体、企业事业单位和其他组织保存的档案。 利用未开放档案的办法，由国家档案行政管理部门和有关主管部门规定。 &nbsp; &nbsp; 向档案馆移交、捐赠、寄存档案的单位和个人，对其档案享有优先利用权，并可对其档案中不宜向社会开放的部分提出限制利用的意见，档案馆应当维护他们的合法权益。 &nbsp; &nbsp; 属于国家所有的档案，由国家授权的档案馆或者有关机关公布；未经档案馆或者有关机关同意，任何组织和个人无权公布。 </p>'),
(37, 1, ' 法律责任', 2, 1, 0, 0, 1412583106, 0, NULL, 31, NULL, 1, '<p> 有下列行为之一的，由县级以上人民政府档案行政管理部门、有关主管部门对直接负责的主管人员或者其他直接责任人员依法给予行政处分；构成犯罪的，依法追究刑事责任： （一）损毁、丢失属于国家所有的档案的； （二）擅自提供、抄录、公布、销毁属于国家所有的档案的； （三）涂改、伪造档案的； （四）违反本法第十六条、第十七条规定，擅自出卖或者转让档案的； （五）倒卖档案牟利或者将档案卖给、赠送给外国人的； （六）违反本法第十条、第十一条规定，不按规定归档或者不按期移交档案的； （七）明知所保存的档案面临危险而不采取措施，造成档案损失的； （八）档案工作人员玩忽职守，造成档案损失的。 </p>'),
(38, 1, '条件、义务与权利', 2, 1, 0, 0, 1412583107, 0, NULL, 31, NULL, 1, '<p> （一）具有中华人民共和国国籍； （二）年满十八周岁； （三）拥护中华人民共和国宪法； （四）具有良好的品行； （五）具有正常履行职责的身体条件； （六）具有符合职位要求的文化程度和工作能力； （七）法律规定的其他条件。 第十二条　公务员应当履行下列义务： （一）模范遵守宪法和法律； （二）按照规定的权限和程序认真履行职责，努力提高工作效率； （三）全心全意为人民服务，接受人民监督； （四）维护国家的安全、荣誉和利益； （五）忠于职守，勤勉尽责，服从和执行上级依法作出的决定和命令； （六）保守国家秘密和工作秘密； （七）遵守纪律，恪守职业道德，模范遵守社会公德； （八）清正廉洁，公道正派； （九）法律规定的其他义务。 第十三条　公务员享有下列权利： （一）获得履行职责应当具有的工作条件； （二）非因法定事由、非经法定程序，不被免职、降职、辞退或者处分； （三）获得工资报酬，享受福利、保险待遇； （四）参加培训； （五）对机关工作和领导人员提出批评和建议； （六）提出申诉和控告； （七）申请辞职； （八）法律规定的其他权利。 </p>'),
(39, 1, '职务与级别', 2, 1, 0, 0, 1412583107, 0, NULL, 31, NULL, 1, '<p> 　国家实行公务员职位分类制度。 公务员职位类别按照公务员职位的性质、特点和管理需要，划分为综合管理类、专业技术类和行政执法类等类别。国务院根据本法，对于具有职位特殊性，需要单独管理的，可以增设其他职位类别。各职位类别的适用范围由国家另行规定。 　国家根据公务员职位类别设置公务员职务序列。 　公务员职务分为领导职务和非领导职务。 领导职务层次分为：国家级正职、国家级副职、省部级正职、省部级副职、厅局级正职、厅局级副职、县处级正职、县处级副职、乡科级正职、乡科级副职。 非领导职务层次在厅局级以下设置。 综合管理类的领导职务根据宪法、有关法律、职务层次和机构规格设置确定。 综合管理类的非领导职务分为：巡视员、副巡视员、调研员、副调研员、主任科员、副主任科员、科员、办事员。 综合管理类以外其他职位类别公务员的职务序列，根据本法由国家另行规定。 　各机关依照确定的职能、规格、编制限额、职数以及结构比例，设置本机关公务员的具体职位，并确定各职位的工作职责和任职资格条件。 　公务员的职务应当对应相应的级别。公务员职务与级别的对应关系，由国务院规定。 公务员的职务与级别是确定公务员工资及其他待遇的依据。 公务员的级别根据所任职务及其德才表现、工作实绩和资历确定。公务员在同一职务上，可以按照国家规定晋升级别。 第 &nbsp;　国家根据人民警察以及海关、驻外外交机构公务员的工作特点，设置与其职务相对应的衔级。 </p>'),
(40, 1, '录     用', 2, 1, 0, 0, 1412583107, 0, NULL, 31, NULL, 1, '<p> 　 录用担任主任科员以下及其他相当职务层次的非领导职务公务员，采取公开考试、严格考察、平等竞争、择优录取的办法。 民族自治地方依照前款规定录用公务员时，依照法律和有关规定对少数民族报考者予以适当照顾。 　中央机关及其直属机构公务员的录用，由中央公务员主管部门负责组织。地方各级机关公务员的录用，由省级公务员主管部门负责组织，必要时省级公务员主管部门可以授权设区的市级公务员主管部门组织。 　报考公务员，除应当具备本法第十一条规定的条件外，还应当具备省级以上公务员主管部门规定的拟任职位所要求的资格条件。 　下列人员不得录用为公务员： &nbsp;曾因犯罪受过刑事处罚的； &nbsp;曾被开除公职的； 有法律规定不得录用为公务员的其他情形的。 　录用公务员，必须在规定的编制限额内，并有相应的职位空缺。 　录用公务员，应当发布招考公告。招考公告应当载明招考的职位、名额、报考资格条件、报考需要提交的申请材料以及其他报考须知事项。 招录机关应当采取措施，便利公民报考。 　招录机关根据报考资格条件对报考申请进行审查。报考者提交的申请材料应当真实、准确。 　公务员录用考试采取笔试和面试的方式进行，考试内容根据公务员应当具备的基本能力和不同职位类别分别设置。 　招录机关根据考试成绩确定考察人选，并对其进行报考资格复审、考察和体检。 体检的项目和标准根据职位要求确定。具体办法由中央公务员主管部门会同国务院卫生行政部门规定。 　招录机关根据考试成绩、考察情况和体检结果，提出拟录用人员名单，并予以公示。 公示期满，中央一级招录机关将拟录用人员名单报中央公务员主管部门备案；地方各级招录机关将拟录用人员名单报省级或者设区的市级公务员主管部门审批。 　录用特殊职位的公务员，经省级以上公务员主管部门批准，可以简化程序或者采用其他测评办法。 　新录用的公务员试用期为一年。试用期满合格的，予以任职；不合格的，取消录用。 </p>'),
(41, 1, '考     核', 2, 1, 0, 0, 1412583108, 0, NULL, 31, NULL, 1, '<p> 　 &nbsp;对公务员的考核，按照管理权限，全面考核公务员的德、能、勤、绩、廉，重点考核工作实绩。 　 &nbsp;公务员的考核分为平时考核和定期考核。定期考核以平时考核为基础。 　 &nbsp;对非领导成员公务员的定期考核采取年度考核的方式，先由个人按照职位职责和有关要求进行总结，主管领导在听取群众意见后，提出考核等次建议，由本机关负责人或者授权的考核委员会确定考核等次。 对领导成员的定期考核，由主管机关按照有关规定办理。 　 &nbsp;定期考核的结果分为优秀、称职、基本称职和不称职四个等次。 定期考核的结果应当以书面形式通知公务员本人。 &nbsp; 　定期考核的结果作为调整公务员职务、级别、工资以及公务员奖励、培训、辞退的依据。 </p>'),
(42, 1, '职务任免', 2, 1, 0, 0, 1412583108, 0, NULL, 31, NULL, 1, '<p> 　 &nbsp;公务员职务实行选任制和委任制。 领导成员职务按照国家规定实行任期制。 　 &nbsp;选任制公务员在选举结果生效时即任当选职务；任期届满不再连任，或者任期内辞职、被罢免、被撤职的，其所任职务即终止。 &nbsp;　委任制公务员遇有试用期满考核合格、职务发生变化、不再担任公务员职务以及其他情形需要任免职务的，应当按照管理权限和规定的程序任免其职务。 　 &nbsp;公务员任职必须在规定的编制限额和职数内进行，并有相应的职位空缺。 &nbsp;　公务员因工作需要在机关外兼职，应当经有关机关批准，并不得领取兼职报酬。 </p>'),
(43, 1, '职务升降', 2, 1, 0, 0, 1412583108, 0, NULL, 31, NULL, 1, '<p> 　 &nbsp; 公务员晋升职务，应当具备拟任职务所要求的思想政治素质、工作能力、文化程度和任职经历等方面的条件和资格。 公务员晋升职务，应当逐级晋升。特别优秀的或者工作特殊需要的，可以按照规定破格或者越一级晋升职务。 　 &nbsp; 公务员晋升领导职务，按照下列程序办理： （一）民主推荐，确定考察对象； （二）组织考察，研究提出任职建议方案，并根据需要在一定范围内进行酝酿； （三）按照管理权限讨论决定； （四）按照规定履行任职手续。 公务员晋升非领导职务，参照前款规定的程序办理。 　 &nbsp;机关内设机构厅局级正职以下领导职务出现空缺时，可以在本机关或者本系统内通过竞争上岗的方式，产生任职人选。 厅局级正职以下领导职务或者副调研员以上及其他相当职务层次的非领导职务出现空缺，可以面向社会公开选拔，产生任职人选。 确定初任法官、初任检察官的任职人选，可以面向社会，从通过国家统一司法考试取得资格的人员中公开选拔。 &nbsp; &nbsp; 公务员晋升领导职务的，应当按照有关规定实行任职前公示制度和任职试用期制度。 　 &nbsp; 公务员在定期考核中被确定为不称职的，按照规定程序降低一个职务层次任职。 </p>'),
(44, 1, '奖     励', 2, 1, 0, 0, 1412583108, 0, NULL, 31, NULL, 1, '<p> 　 &nbsp;对工作表现突出，有显著成绩和贡献，或者有其他突出事迹的公务员或者公务员集体，给予奖励。奖励坚持精神奖励与物质奖励相结合、以精神奖励为主的原则。 公务员集体的奖励适用于按照编制序列设置的机构或者为完成专项任务组成的工作集体。 　 &nbsp;公务员或者公务员集体有下列情形之一的，给予奖励： （一）忠于职守，积极工作，成绩显著的； （二）遵守纪律，廉洁奉公，作风正派，办事公道，模范作用突出的； （三）在工作中有发明创造或者提出合理化建议，取得显著经济效益或者社会效益的； （四）为增进民族团结、维护社会稳定做出突出贡献的； （五）爱护公共财产，节约国家资财有突出成绩的； （六）防止或者消除事故有功，使国家和人民群众利益免受或者减少损失的； （七）在抢险、救灾等特定环境中奋不顾身，做出贡献的； （八）同违法违纪行为作斗争有功绩的； （九）在对外交往中为国家争得荣誉和利益的； （十）有其他突出功绩的。 </p>'),
(45, 1, '惩     戒', 2, 1, 0, 0, 1412583109, 0, NULL, 31, NULL, 1, '<p> 　处分分为：警告、记过、记大过、降级、撤职、开除。 　对公务员的处分，应当事实清楚、证据确凿、定性准确、处理恰当、程序合法、手续完备。 公务员违纪的，应当由处分决定机关决定对公务员违纪的情况进行调查，并将调查认定的事实及拟给予处分的依据告知公务员本人。公务员有权进行陈述和申辩。 &nbsp; 处分决定机关认为对公务员应当给予处分的，应当在规定的期限内，按照管理权限和规定的程序作出处分决定。处分决定应当以书面形式通知公务员本人。 　公务员在受处分期间不得晋升职务和级别，其中受记过、记大过、降级、撤职处分的，不得晋升工资档次。 受处分的期间为：警告，六个月；记过，十二个月；记大过，十八个月；降级、撤职，二十四个月。 受撤职处分的，按照规定降低级别。 　公务员受开除以外的处分，在受处分期间有悔改表现，并且没有再发生违纪行为的，处分期满后，由处分决定机关解除处分并以书面形式通知本人 </p>'),
(46, 1, '培     训', 2, 1, 0, 0, 1412583109, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;机关根据公务员工作职责的要求和提高公务员素质的需要，对公务员进行分级分类培训。 国家建立专门的公务员培训机构。机关根据需要也可以委托其他培训机构承担公务员培训任务。 　 &nbsp;机关对新录用人员应当在试用期内进行初任培训；对晋升领导职务的公务员应当在任职前或者任职后一年内进行任职培训；对从事专项工作的公务员应当进行专门业务培训；对全体公务员应当进行更新知识、提高工作能力的在职培训，其中对担任专业技术职务的公务员，应当按照专业技术人员继续教育的要求，进行专业技术培训。 国家有计划地加强对后备领导人员的培训。 　 &nbsp;公务员的培训实行登记管理。 公务员参加培训的时间由公务员主管部门按照本法第六十一条规定的培训要求予以确定。 公务员培训情况、学习成绩作为公务员考核的内容和任职、晋升的依据之一 </p>'),
(47, 1, '交流与回避', 2, 1, 0, 0, 1412583109, 0, NULL, 31, NULL, 1, '<p> 　 &nbsp;国家实行公务员交流制度。 公务员可以在公务员队伍内部交流，也可以与国有企业事业单位、人民团体和群众团体中从事公务的人员交流。 交流的方式包括调任、转任和挂职锻炼。 　国有企业事业单位、人民团体和群众团体中从事公务的人员可以调入机关担任领导职务或者副调研员以上及其他相当职务层次的非领导职务。调任人选应当具备本法第十一条规定的条件和拟任职位所要求的资格条件，并不得有本法第二十四条规定的情形。调任机关应当根据上述规定，对调任人选进行严格考察，并按照管理权限审批，必要时可以对调任人选进行考试。 　公务员在不同职位之间转任应当具备拟任职位所要求的资格条件，在规定的编制限额和职数内进行。 对省部级正职以下的领导成员应当有计划、有重点地实行跨地区、跨部门转任。 对担任机关内设机构领导职务和工作性质特殊的非领导职务的公务员，应当有计划地在本机关内转任。 　根据培养锻炼公务员的需要，可以选派公务员到下级机关或者上级机关、其他地区机关以及国有企业事业单位挂职锻炼。 公务员在挂职锻炼期间，不改变与原机关的人事关系。 　公务员应当服从机关的交流决定。 公务员本人申请交流的，按照管理权限审批。 　公务员之间有夫妻关系、直系血亲关系、三代以内旁系血亲关系以及近姻亲关系的，不得在同一机关担任双方直接隶属于同一领导人员的职务或者有直接上下级领导关系的职务，也不得在其中一方担任领导职务的机关从事组织、人事、纪检、监察、审计和财务工作。 　公务员担任乡级机关、县级机关及其有关部门主要领导职务的，应当实行地域回避，法律另有规定的除外。 </p>'),
(48, 1, '工资福利保险', 2, 1, 0, 0, 1412583110, 0, NULL, 31, NULL, 1, '<p> &nbsp; 公务员工资制度贯彻按劳分配的原则，体现工作职责、工作能力、工作实绩、资历等因素，保持不同职务、级别之间的合理工资差距。 国家建立公务员工资的正常增长机制。 　 公务员工资包括基本工资、津贴、补贴和奖金。 公务员按照国家规定享受地区附加津贴、艰苦边远地区津贴、岗位津贴等津贴。 公务员按照国家规定享受住房、医疗等补贴、补助。 公务员在定期考核中被确定为优秀、称职的，按照国家规定享受年终奖金。 公务员工资应当按时足额发放。 　 公务员的工资水平应当与国民经济发展相协调、与社会进步相适应。 国家实行工资调查制度，定期进行公务员和企业相当人员工资水平的调查比较，并将工资调查比较结果作为调整公务员工资水平的依据。 　 &nbsp;公务员按照国家规定享受福利待遇。国家根据经济社会发展水平提高公务员的福利待遇。 公务员实行国家规定的工时制度，按照国家规定享受休假。公务员在法定工作日之外加班的，应当给予相应的补休。 　 国家建立公务员保险制度，保障公务员在退休、患病、工伤、生育、失业等情况下获得帮助和补偿。 公务员因公致残的，享受国家规定的伤残待遇。公务员因公牺牲、因公死亡或者病故的，其亲属享受国家规定的抚恤和优待。 　 &nbsp;任何机关不得违反国家规定自行更改公务员工资、福利、保险政策，擅自提高或者降低公务员的工资、福利、保险待遇。任何机关不得扣减或者拖欠公务员的工资。 　 &nbsp;公务员工资、福利、保险、退休金以及录用、培训、奖励、辞退等所需经费，应当列入财政预算，予以保障。 </p>'),
(49, 1, '辞职辞退', 2, 1, 0, 0, 1412583110, 0, NULL, 31, NULL, 1, '<p> 　 &nbsp;公务员辞去公职，应当向任免机关提出书面申请。任免机关应当自接到申请之日起三十日内予以审批，其中对领导成员辞去公职的申请，应当自接到申请之日起九十日内予以审批。 　 &nbsp;公务员有下列情形之一的，不得辞去公职： 未满国家规定的最低服务年限的； 在涉及国家秘密等特殊职位任职或者离开上述职位不满国家规定的脱密期限的； 重要公务尚未处理完毕，且须由本人继续处理的； 正在接受审计、纪律审查，或者涉嫌犯罪，司法程序尚未终结的； 法律、行政法规规定的其他不得辞去公职的情形。 　 &nbsp;担任领导职务的公务员，因工作变动依照法律规定需要辞去现任职务的，应当履行辞职手续。 担任领导职务的公务员，因个人或者其他原因，可以自愿提出辞去领导职务。 领导成员因工作严重失误、失职造成重大损失或者恶劣社会影响的，或者对重大事故负有领导责任的，应当引咎辞去领导职务。 领导成员应当引咎辞职或者因其他原因不再适合担任现任领导职务，本人不提出辞职的，应当责令其辞去领导职务。 　 &nbsp;公务员有下列情形之一的，予以辞退： （一）在年度考核中，连续两年被确定为不称职的； （二）不胜任现职工作，又不接受其他安排的； （三）因所在机关调整、撤销、合并或者缩减编制员额需要调整工作，本人拒绝合理安排的； （四）不履行公务员义务，不遵守公务员纪律，经教育仍无转变，不适合继续在机关工作，又不宜给予开除处分的； （五）旷工或者因公外出、请假期满无正当理由逾期不归连续超过十五天，或者一年内累计超过三十天的。 </p>'),
(50, 1, '附则', 2, 1, 0, 0, 1412583110, 0, NULL, 31, NULL, 1, '<p> 　对无营业执照或者已被依法吊销营业执照，有劳动用工行为的，由劳动保障行政部门依照本规定实施劳动保障监察。 　本规定自2005年2月1日起施行。原《劳动监察规定》（劳部发〔1993〕167号）、《劳动监察程序规定》（劳部发〔1995〕457号）、《处理举报劳动违法行为规定》（劳动部令第5号，1996年12月17日）同时废止。 </p>'),
(51, 1, '2014年第二期青年就业见习训练全市集中招训活动将于8月20日开展', 3, 1, 0, 0, 1412583110, 0, NULL, 31, NULL, 1, '<p> 为进一步开发人力资源，促进广大青年实现就业，市就业管理办公室与市人才管理办公室组织实施2014年的青年就业见习训练。第二期集中招训活动将于8月20日至9月10日接受广大青年报名。 一、青年就业见习训练简介 自2008年以来，由市就业管理办公室组织实施的“青年就业见习训练”。主要向户籍35岁以下的未就业青年(含普通高校毕业生)提供一次性的资助就业见习训练。 见习训练基地单位将提供各类技术类、管理类岗位，将见习人员直接安排到相关岗位上开展训练，训练期一般为2-3个月。在见习训练期内，由见习单位为学员提供相关岗位理论知识和实际操作能力的训练。训练结束后，市就业管理办公室对完成见习训练的人员发放《青年就业见习训练结业证书》；见习单位相应岗位优先招录训练合格的学员，学员与单位达成就业意向的，签订一年以上期限的无试用期劳动合同。 根据《东莞市人力资源局青年就业见习训练操作办法》（东人发〔2011〕21号）有关规定，在参训期内政府给予见习人员每人每天15元的生活津贴。本地生源普通高校毕业生参加就业见习训练，凭“东莞生源学历确认证明”，在见习期间，可以取得由财政给予见习人员每人每天35元的生活补贴。同时，所有见习单位给见习人员提供不低于1050元/月的见习训练期补助（2013年5月起）。 二、报名方式及有关事项 （一）报名方式： 1、8月20日集中接受报名： 报名时间：20日上午9时至下午12时。 报名地点：全市32个镇（街）人力资源服务中心（地址：镇街人力资源分局内）、松山湖人力资源服务中心（地址：东莞市松山湖高新技术产业开发区礼宾路1号行政办事中心附楼，东莞市人力资源局松山湖分局内）和市职业介绍服务中心（市人力资源局综合楼二楼就业服务大厅，地址：东莞市东城堑头育兴路）均可报名，由各人力资源服务中心人员代收报名资料并及时传递给相关训练单位。报名点具体地址，详见《报名点信息》。 2、分散报名：8月21日-9月10日，有意向的人员可直接与青年就业见习训练单位、见习训练单位所在地人力资源服务中心联系报名。具体联系方式（联系人、联系电话及电子邮箱）见《2014年第二期青年见习训练单位及训练岗位名录》。 （二）报名需提交资料： 个人身份证复印件、户口簿复印件各一份，有关学历资料复印件一份（毕业生推荐表或学历证书复印件）。 （三）有关资料索取： 《2014第二期青年见习训练单位及训练岗位名录》及报名表格可在市、镇人力资源服务中心索取，或在“东莞市就业服务网http://jyfw.dg.gov.cn/”下载。 </p>'),
(52, 1, '2014年第一期青年就业见习训练全市集中招训活动将于5月20日开展', 3, 1, 0, 0, 1412583110, 0, NULL, 31, NULL, 1, '<p> 为进一步开发人力资源，促进广大青年实现就业，市就业管理办公室计划于5月20日（星期二）在全市就业服务机构统一开展“2014第一期青年就业见习训练全市集中招训日活动”，接受广大青年报名。 目前已收集招商银行、百胜餐饮（必胜客）、东莞证券、以纯集团、东糖集团、玖龙纸业、东莞雀巢等256家莞企业的5591个适合青年的见习训练岗位，欢迎各位青年朋友踊跃报名。 （一）报名方式： 1、5月20日集中接受报名： 全市32个镇（街）人力资源服务中心、松山湖人力资源服务中和市职业介绍服务中心均可报名。报名点具体地址，详见《报名点信息》。 2、分散报名：5月21日-6月10日，有意向的人员可直接与青年就业见习训练单位、见习训练单位所在地人力资源服务中心联系报名。具体联系方式（联系人、电子邮箱）见《2014年第一期青年见习训练单位及训练岗位名录》。 （二）报名需提交资料： 个人身份证复印件、户口簿复印件各一份，有关学历资料复印件一份（毕业生推荐表或学历证书复印件）。 （三）有关资料索取： 《2014第一期青年见习训练单位及训练岗位名录》及报名表格（见附件）可在市、镇人力资源服务中心索取，或在“东莞市就业服务网http://jyfw.dg.gov.cn/”下载。 </p>'),
(53, 1, '2013年第二期青见全市集中招训活动将于7月20日开展', 3, 1, 0, 0, 1412583111, 0, NULL, 31, NULL, 1, '<p> 为进一步开发我市新成长人力资源，促进青年提高就业技能、实现就业，市就业管理办公室计划于7月20日（星期六）在全市就业服务机构统一开展“2013第二期青年就业见习训练全市集中招训日活动”，接受广大青年报名。今年7月起，已将非莞籍的2013年应届普通高校毕业生纳入青年就业见习训练的参训对象。 目前已收集东莞证券、光大集团、大地通讯、金叶珠宝、众生药业、永正图书等240家莞企业的3459个适合青年的见习训练岗位，欢迎各位青年朋友踊跃报名。 （一）报名方式： 1、集中现场报名： 报名时间：7月20日（星期六）9：00-12：00。 报名地点：全市32个镇（街）人力资源服务中心、松山湖人力资源服务中心以及市职业介绍服务中心均可接受现场报名。报名点具体地址，详见《报名点信息》。 2、分散联系报名：7月22日-7月31日，有意向的人员可直接与青年见习训练单位、见习训练单位所在地人力资源服务中心联系报名。具体联系方式（电子邮箱）见《2013第二期青年见习训练单位及训练岗位名录》。 （二）报名需提交资料： 个人身份证复印件、户口簿复印件各1份，有关学历资料复印件一份（毕业生推荐表或学历证书复印件）。非莞籍2013年应届普通高校毕业生参加青年就业见习训练，只须提交个人身份证复印件、学历证书复印件。 （三）有关资料索取： 《2013第二期青年见习训练单位及训练岗位名录》及报名表格可在市职业介绍服务中心和各镇（街）人力资源服务中心索取，或点击下载。 咨询电话：22697037，22697710 </p>'),
(54, 1, '2014年4月17日东莞市技师学院开展创业培训班一期', 3, 1, 0, 0, 1412583111, 0, NULL, 31, NULL, 1, '<p> 东莞市就业管理办公室委托东莞市技师学院在东莞市技师学院（莞龙路36号）开展创业培训班，培训人数共28人，培训时间：2014年4月17日至4月30日。 &nbsp; </p>'),
(55, 1, '关于调整就业困难人员工资差额补助、 灵活就业补助、大中专毕业生企业就业 岗位津贴三项政策申领操作的通告', 3, 1, 0, 0, 1412583111, 0, NULL, 31, NULL, 1, '<p> 为完善公共就业补贴申领制度，从2013年9月1日起，调整就业困难人员工资差额补助、就业困难人员灵活就业补助、大中专毕业生企业就业岗位津贴三项政策的申领操作，具体调整内容通告如下： 一、就业困难人员工资差额补助、灵活就业补助： 申领时限为4个月内，申请人应当在4个月内提交补助申请，逾期不受理。（例如：申领2013年5月份的就业补助，必须在2013年9月底前到村或社区人力资源服务站提交申请资料。） 符合申领条件的人员，其2013年5月份以前的就业补助申请，也请务必于2013年9月30日前提交，逾期不受理。 二、大中专毕业生企业就业岗位津贴： （一）申领地调整为户籍所在地，由申请人本人到户籍所在镇（街）人力资源分局提交津贴申领资料。 （二）申领时限调整为毕业三年后的3个月内，申请人应当在毕业三年后的3个月内到人力资源分局完成提交津贴申请，逾期不受理。（例如：申领人2010年7月1日毕业，必须在2013年9月30日前完成提交申领。） 对2008年至2010年6月30日期间毕业且符合申领条件的人员，也请务必于2013年9月30日前提交津贴申请，逾期不受理。 三、实施就业实地核查及违规处罚： 对于初次申领补贴的，申请人提交申请资料后，将对每位申请人的就业情况进行工作单位实地核查，经实地核查就业（收入）等相关情况符合申领补助要求的，才同意申领补助（津贴）。 对于2013年5月份前已申领补贴并延续申领的，也将进行实地核查，如发现申领人有弄虚作假行为的，将视情况作出责令退款，以及不得再申领该项就业补助的处罚。 2013年5月29日 </p>'),
(56, 1, '我市29家企业赴江西省开展校园招聘系列活动', 3, 1, 0, 0, 1412583112, 0, NULL, 31, NULL, 1, '<p> 为做好校企合作后续跟踪服务，帮助我市企业开发人力资源，9月26-28日，市职业介绍服务中心组织了29家有用工需求的企业赴江西省开展校企合作洽谈及校园现场招聘会活动。 参加此次活动的企业有台达电子（东莞）有限公司、广东星弛光电科技有限公司、东莞新能源科技有限公司、快意电梯股份有限公司、信义玻璃控股有限公司等29家，涵盖电子、能源、制造业等多个行业，提供了机械、电子、化工、设计、文秘、财务、企业管理等1500多个适合毕业生就业的岗位。 在江西科技学院、华东交通大学及南昌航空大学的三场专场招聘会中，入场求职的毕业生人数近1万多人次，其中有1022名求职者与企业达成了初步就业意向。 </p>'),
(57, 1, '7月10日市职介中心现场招聘会简讯', 3, 1, 0, 0, 1412583112, 0, NULL, 31, NULL, 1, '<p> 7月10日上午，市职业介绍服务中心在市就业服务大厅举办了“就业服务日”现场招聘会活动，万士达液晶显示器有限公司、广东国泰国际旅行社有限公司、中镓半导体科技有限公司、远梦家居用品股份有限公司、光明中学等25家企业入场设摊，现场进行面试招聘。招聘会提供了工程师、财务人员、策划、翻译、厨师、保安等各类岗位近340个，吸引200多人次进场求职，其中约78名求职者与企业达成初步就业意向。 当日全市各镇街分别以现场招聘会、集中岗位推荐、职业指导讲座、就业政策宣传等方式开展“就业服务日”活动，共提供9645个招聘岗位。 </p>');
INSERT INTO `mz_document` (`id`, `uid`, `title`, `category_id`, `display`, `status`, `views`, `create_time`, `update_time`, `from`, `level`, `description`, `order_num`, `content`) VALUES
(58, 1, '市人力资源局与新疆建设兵团第三师劳动保障局签订了人力资源劳务合作协议', 3, 1, 0, 0, 1412583112, 0, NULL, 31, NULL, 1, '<p> 为做好东莞市对口支援新疆建设兵团第三师工作，落实党中央、国务院和省委、省政府对口支援新疆的工作部署， 2014年6月20日，我局相关领导一行3人在省人社厅的组织安排下，赴新疆与建设兵团第三师劳动和社会保障局进行对接洽谈，经双方充分沟通与协商，最终签订了《东莞-新疆兵团第三师人力资源劳务合作协议书》。《东莞-新疆兵团第三师人力资源劳务合作协议书》就双方加强劳务合作达成一致意见，明确建立长期稳定的劳务合作关系，逐步加强人力资源信息交流共享，有序开展劳务对接活动，进一步完善在莞就业的新疆籍劳动力维稳维权联动协调处理机制。本次协议的签订将促进东莞与新疆兵团三师的劳务合作交流，为我局更好地完成对口支援新疆兵团三师工作任务打下坚实基础。 </p>'),
(59, 1, '新疆兵团第三师劳动和社会保障局来莞对接考察劳务合作事项', 3, 1, 0, 0, 1412583112, 0, NULL, 31, NULL, 1, '<p> 为贯彻落实党中央、国务院和省委、省政府对口支援新疆以及东莞市对口支援新疆建设兵团第三师的工作部署，加快推进劳务帮扶和人力资源合作，2014年6月4日，新疆兵团第三师劳动和社会保障局考察团一行4人到我市洽谈劳务合作，并到企业实地考察。 市人力资源局吴柏安副局长热情接待了新疆考察团，并进行了亲切的座谈。吴柏安副局长在座谈会上介绍了东莞市的基本情况和我市对少数民族地区人员和新疆籍劳动力的就业服务情况。会上双方洽谈并最终确定了《东莞-新疆兵团第三师人力资源劳务合作协议书》内容。6月5日，新疆考察团赴东莞绿洲鞋业有限公司进行实地考察，听取了企业介绍招用新疆籍员工的管理模式和新疆籍带队干部、员工介绍情况。企业负责人对考察团提出的关于工资待遇、社保保障等问题进行了详细的解答。新疆考察团还实地考察了企业的用工环境、生活设施等，对企业的用工环境和用工管理服务表示满意。 </p>'),
(60, 1, '东莞韶关两市人力资源部门互访落实对口帮扶工作', 3, 1, 0, 0, 1412583112, 0, NULL, 31, NULL, 1, '<p> 2014年5月29-30日，东莞市人力资源局吴柏安副局长一行赴韶关市开展对口帮扶工作。29日，我局一行4人与韶关市人社局罗任群副局长及相关科室负责人座谈，共同研究如何落实《关于进一步落实东莞-韶关十大合作领域对接项目工作的通知》（东府办[2014]132号）。同时与韶关市人社局一同拜访了莞韶对口帮扶指挥部，汇报了人力资源对口帮扶工作，听取指挥部对人力资源部门落实合作项目工作的要求。30日，赴对口帮扶的乳源瑶族自治县乳城镇共和村实地了解劳务帮扶工作的进展情况，并与贫困帮扶人员面对面了解其就业需求。接下来，两市人力资源部门将共同为帮扶人员做好就业推荐和跟踪服务。 为尽快推动落实对口帮扶工作任务，确定双方具体合作事宜，2014年6月4日，韶关市人社局罗任群副局长一行6人回访我局。吴柏安副局长和相关科室单位负责人与韶关人社局进行了亲切座谈交流，重点就如何落实两地市政府签订的《东莞韶关全面合作交流框架协议》进行详细沟通。经过紧张热烈的讨论，双方最终确定了协议内容和合作项目，为下来制定合作计划、工作方案、落实对口帮扶工作打下坚实基础。 </p>'),
(61, 1, '市就业管理办公室赴莞韶产业园实地检查农村劳动力转移就业工作情况', 3, 1, 0, 0, 1412583113, 0, NULL, 31, NULL, 1, '<p> 为贯彻落实《中共广东省委办公厅_广东省人民政府办公厅关于调整珠三角地区与粤东西北地区对口帮扶关系的通知》(粤委办[2013]27号)和《东莞市农村劳动力技能培训及转移就业实施方案》（东农工办〔2009〕1号），鼓励韶关籍农村劳动力向莞韶产业园转移就业，东莞与韶关两地人力资源部门积极做好劳动力转移相关工作，大力实施对口劳务帮扶。2014年5月28-30日东莞市就业管理办公室赴韶关市对莞韶产业园4051名韶关籍农村劳动力转移就业进行抽查验收，分别抽查核实了“韶关旭日国际有限公司”、“韶关科艺创意工业有限公司”、“韶关康瑞科技有限公司”和南雄珠玑工业园等企业的转移劳动力，其中符合相关条件的3993名转移就业的韶关籍农村劳动力将获得我市的输送补贴。 </p>'),
(62, 1, '我市企业赴贵州开展校企合作洽谈', 3, 1, 0, 0, 1412583113, 0, NULL, 31, NULL, 1, '<p> 为帮助企业开发人力资源，满足企业的用工需求，2014年5月28日，受贵阳市白云区人民政府邀请，我市职业介绍服务中心组织了信义玻璃控股有限公司、东莞华宝鞋业有限公司、顺丰速运（东莞）有限公司、东莞市永强汽车制造有限公司、东莞宇宙电路板设备有限公司、长荣玩具东莞有限公司、成翔电子（东莞）有限公司、联业制衣（东莞）有限公司共8家有中长期校企合作意向的企业，到贵阳市白云区职业技术学校开展“白云区职业教育产教融合、政校企合作现场洽谈会”活动。 此次活动中，共有贵阳地区约15家中专中技学校参加，在参观了白云区职业技术学校校办工厂后，企业与参会学校进行了校企合作的洽谈，分享了校企合作经验，提出了今后校企合作的发展方向，促进了我市企业与贵阳地区学校的深层次校企合作。 </p>'),
(63, 1, '组织企业赴茂名开展校企合作洽谈及现场招聘活动', 3, 1, 0, 0, 1412583113, 0, NULL, 31, NULL, 1, '<p> 4月14日，市就业服务部门组织广东生益科技股份有限公司、广东大地通讯连锁服务有限公司、东莞豪门大饭店等13家企业，赴茂名市开展校企合作及现场招聘活动。活动当天，企业分别与茂名市高级技工学校、茂名市第一职业技术学校进行了校企合作洽谈，并参加了由茂名市电白县人民政府主办的“2014‘南粤春暖’就业服务活动电白专场招聘会”。 </p>'),
(64, 1, '市人力资源局召开2014年就业工作座谈会', 3, 1, 0, 0, 1412583114, 0, NULL, 31, NULL, 1, '<p> 3月18日上午，市人力资源局召开2014年全市人力资源系统就业工作座谈会。市级就业口全体工作人员以及各镇（街）人力资源分局分管就业领导、培训就业办主任和业务骨干，共160多人参加了会议。会上，部分镇（街）作了就业工作经验介绍，吴柏安副局长总结了2013年就业工情况及布置2014年就业工作要点。 </p>'),
(65, 1, '组织企业参加揭阳市2014年“南粤春暖”招聘会', 3, 1, 0, 0, 1412583114, 0, NULL, 31, NULL, 1, '<p> 3月8日，市就业管理办公室组织我市企业赴揭阳市，参加揭阳市2014年“南粤春暖”招聘会。 我市参加招聘会的有东莞普克运动用品有限公司、东莞正阳电子有限公司等20家优质企业，提供技术类、管理类等832个招聘岗位，岗位工资收入都在2500元以上。招聘会设在揭阳学院，应聘者以毕业生为主，他们多表示此次参加招聘的东莞企业在薪资福利待遇方面较具吸引力。据现场统计，招聘会共吸引了1520多名毕业生进场应聘，共有116名毕业生与我市企业达成初步就业意向。 </p>'),
(66, 1, '举办东莞－韶关“南粤春暖”就业服务专场招聘会', 3, 1, 0, 0, 1412583114, 0, NULL, 31, NULL, 1, '<p> 为落实东莞和韶关两市就业服务部门劳务洽谈时达成的工作共识，开展对口劳务帮扶，促进韶关劳动力来莞就业。3月5日，两市就业服务部门在韶关市翁源县举办“东莞－韶关‘南粤春暖’就业服务专场招聘会”。 我市就业服务部门组织常平、横沥、东坑和莞城等4个镇街的32家优质企业赴韶关参加招聘会，提供2000多个招聘岗位。此次招聘会吸引了韶关本地劳动力1000多人来求职应聘，100多名求职者与我市企业达成就业意向。 </p>'),
(67, 1, '积极组织企业赴外招工 搭建劳务对接平台', 3, 1, 0, 0, 1412583114, 0, NULL, 31, NULL, 1, '<p> 为帮助我市企业解决节后用工需求，市就业服务部门节后积极组织开展“春风行动”系列招聘活动，其中2月中旬以来，已组织企业赴梅州、韶关、汕尾等市参加招聘会或进行劳务洽谈对接，致力为企业搭建劳务对接平台，帮助企业招工。 2月18日，市人力资源局组织我市16家大型优质企业赴梅州市参加“春风行动”现场招聘会。局相关领导在招聘会现场深入了解节后企业用工情况，指导企业在用工紧张时期更要做好员工的人文关怀，让员工对企业有归属感，减少员工流失率、保持企业用工稳定。据统计，此次招聘活动共有100多名求职者与我市企业达成就业意向。 2月20日至21日，市局和市就业管理办公室相关领导，到韶关市开展对口劳务洽谈对接洽谈，韶关市人力资源和社会保障局罗任群副局长等领导与我市就下来如何开展对口劳务合作、劳动力转移等相关事宜进行了详细商谈。经双方深入商谈及交换意见，初步达成如下合作意向：一是加强双方劳务合作，建立劳动力输入输出长效机制，由韶关根据东莞企业需求情况组织输送劳动力，确保完成劳动力转移帮扶任务；二是提高双方人力资源市场信息对接效率，建立健全两地人力资源市场供求信息定期交流机制；三是在韶关组织开展有针对性的东莞企业招聘专场，促进韶关劳动力来东莞就业，第一场计划于3月初进行；四是加强劳动力培训，推动两市校企合作，鼓励韶关的技校、中职学校向东莞企业输送毕业生，并确定3月底韶关人力资源部门将组织当地中职学校、技校参加我市第五届校企合作洽谈会，推动我市企业与当地学校开展校企合作，为企业输送合适人才。 2月19-20日，市职业介绍服务中心组织了10家有用工需求的优质企业赴汕尾市参加由汕尾市人力资源和社会保障局举办“2014年 ‘南粤春暖’企业用工招聘会”，并与汕尾市建立长期劳务合作关系。 </p>'),
(68, 1, '喻丽君副市长深入人力资源市场调研节后用工情况', 3, 1, 0, 0, 1412583115, 0, NULL, 31, NULL, 1, '<p> 2月8日上午，市政府副市长喻丽君到智通人才市场，实地调研我市节后人力资源市场供求情况，市政府副秘书长张春扬、市人力资源局局长游其晃、副局长吴柏安等领导参加了调研。 在智通人才市场，喻丽君副市长听取了市场负责人介绍目前企业招工、员工返岗等有关情况，视察了招聘会现场开展情况。当天共有200多家企业设摊招聘，场内气氛热烈，各招聘摊位前人头攒动，据统计，现场有2000多名求职者进场应聘。视察期间，喻丽君副市长与招聘企业代表和求职者交谈，了解企业招工、求职者应聘等相关情况。喻丽君副市长并指出每年春节后是企业招聘和异地务工人员求职的高峰期，希望社会各大人力资源市场为不同层次的企业和求职者提供多元化的服务，大力宣传我市良好用工环境，搭建企业与求职者对接和交流平台，促进劳动力供求对接。 接下来，我市人力资源部门将继续密切关注企业员工返岗情况以及人力资源市场的供求情况，及时做好相关就业服务。人力资源部门计划节后一个月举办“南粤春暖”、“春风行动”系列免费招聘会35场，同时现场提供就业咨询及服务、派发《企业节后用工信息》专刊、《求职指南》等各类资料。计划3月底举办第五届“东莞市校企合作洽谈会”，目前正开展相关筹备工作。 </p>'),
(69, 1, '46名青年就业见习训练学员参加2014年第一期 “阳光心态”活动', 3, 1, 0, 0, 1412583115, 0, NULL, 31, NULL, 1, '<p> 为提升青年就业见习训练学员的综合素质，培养社会契约意识、提高人际关系适应性，1月14日，市就业管理办公室、市职业介绍服务中心组织46名青年就业见习训练学员参加2014年第一期“阳光心态”训练活动。活动邀请了拥有职业指导师、心理咨询师等多项资历、并担任多家媒体心理专栏顾问的许老师现场授课。许老师通过自身多年从事企业高层管理、培训的经验，以“阳光心态和快乐工作”为主题，讲授让学员及时发现并化解压力、了解并提升自己的优势等内容。课后，学员纷纷表示受益匪浅。 </p>'),
(70, 1, '开展“平安回家、相约东莞”就业服务活动', 3, 1, 0, 0, 1412583115, 0, NULL, 31, NULL, 1, '<p> 为使返莞务工人员掌握我市企业节后招工信息，引导节后有序流动，同时促进求职与招聘对接，人力资源服务部门将“南粤春暖”就业服务活动前移，于1月16日至17日期间，在全市各镇（街）交通站场及返乡客流集聚的地方统一开展“平安回家、相约东莞”就业服务活动，向返乡人员免费派发《2014年东莞市企业节后用工需求信息》和《求职指南》。 在活动期间，共组织了380名就业服务工作人员统一佩戴工作证和印有“就业服务、相约东莞”彩带，在各镇街服务站场向返乡人员送上节后用工信息（收集了全市各镇街500多家企业的15万个用工信息）和《求职须知》（包括了务工须知、全市人力资源公共服务机构和正规人力资源市场的地址、联系电话等信息），为广大返乡员工提供企业节后最新用工招聘信息，希望通过返乡员工，将东莞的企业节后用工需求信息带回其家乡，引导节后务工人员有序流动的同时，亦帮助企业广泛发布岗位信息，帮助企业招人。春运第一天（1月16日），市领导在巡视万江汽车站时，看到了就业服务工作人员派发资料的情况，详细了解了服务开展情况，并充分肯定了“平安回家、相约东莞”就业服务活动此项举措。 据统计，活动期间，全市共派发《2014年东莞市企业节后用工需求信息》和《求职指南》30万多份。 </p>'),
(71, 1, '组织企业赴汕尾参加高校毕业生专场招聘会', 3, 1, 0, 0, 1412583115, 0, NULL, 31, NULL, 1, '<p> 12月19至20日，市职业介绍服务中心组织10家东莞企业赴汕尾市，参加由汕尾市人力资源和社会保障局举办的“2013年高校毕业生（2014届）汕尾专场招聘会”。 参加本次活动的东莞企业包括茂瑞电子（东莞）有限公司、广东唯美陶瓷有限公司、明门（中国）幼童用品有限公司、台丰印刷电路板(东莞)有限公司等10家企业，提供各类工程师、研发技术员、模具设计人员、翻译、客户经理、财务人员、储备干部及文员等750多个需求岗位。 招聘会共吸引了2000多名毕业生进场应聘，应聘毕业生多表示此次参加招聘的东莞企业在薪资福利待遇方面非常具有吸引力。据现场统计，共有150多名毕业生与我市企业达成初步就业意向，并表示近期将到达成意向的企业就业。 </p>'),
(72, 1, '成功举办“东莞名企湖南行”校园招聘系列活动', 3, 1, 0, 0, 1412583115, 0, NULL, 31, NULL, 1, '<p> 12月16至18日，由市人力资源局主办、市职业介绍服务中心承办的“东莞名企湖南行” 校园招聘系列活动在湖南省长沙市成功举办。 本次活动共组织了华坚集团华宝鞋业有限公司、光宝电源科技(东莞)有限公司、广东比伦生活用纸有限公司等18家有中长期用工需求的企业参加，提供各类工程师、研发技术员、财务人员、翻译、客户经理等1100多个需求岗位。 活动期间，市人力资源局带领企业先后参加了由湖南省教育厅在湖南商务职业技术学院举办的行业专场招聘会，以及在长沙学院、湖南农业大学及湖南农业大学东方科技学院举办的2场东莞名企专场招聘会。吴柏安副局长出席了招聘活动，并与学院领导、企业人事主管深入交流学院毕业生情况和企业招用工情况。据初步统计，此次校园招聘活动共有837名毕业生现场与企业达成初步就业意向。此外，还有部分企业与学院达成了“订单式”校企合作培训意向，建立了长期合作关系。 现场招聘会结束后，市人力资源局还组织企业与湖南科技职业学院、湖南工程职业技术学院、湖南外国语职业学院、湖南建筑高级技工学校、长沙市电子工业学校等8家院校进行校企合作交流对接，推动东莞企业与湖南省院校的长期合作发展。 </p>'),
(73, 1, '百名青年就业见习训练学员参加“阳光心态”训练活动', 3, 1, 0, 0, 1412583116, 0, NULL, 31, NULL, 1, '<p> 为向初入职场的青年就业见习训练学员提供更多思想上的引导和帮助，提升学员的综合素质、凝聚力和相互协作精神，市就业管理办公室、市职业介绍服务中心组织105名在纺织行业进行就业见习训练的青年学员，于11月30日和12月10日分两批在东莞德永佳纺织制衣有限公司参加2013年“阳光心态”训练活动。活动邀请某社工组织的心理咨询师为学员授课。授课老师围绕着如何保持健康的阳光心态、积极地快乐工作的主题，从心理学角度与学员们共同展开深入的探讨与分析。授课现场气氛活跃，参训学员纷纷表示通过这次现场交流活动获益良多。 </p>'),
(74, 1, '成功举办“东莞名企湖北行”校园招聘系列活动', 3, 1, 0, 0, 1412583116, 0, NULL, 31, NULL, 1, '<p> 11月12至14日，由市人力资源局主办、市职业介绍服务中心承办的“东莞名企湖北行”校园招聘活动在湖北荆州成功举办。 湖北是教育大省，而荆州是除省会武汉之外的教育强市，本次活动，市人力资源局分别在长江大学工程技术学院、文理学院举办“东莞名企专场校园招聘会”，吴柏安副局长和长江大学工程技术学院孙首臣院长出席了招聘会。参加本次招聘活动的企业有东莞市万士达液晶显示器有限公司、天虹商场有限公司、康帝国际酒店有限公司、日本电产（东莞）有限公司等29家优质企业，提供各类工程师、研发技术员、模具设计员、翻译、客户经理等2400多个需求岗位。 此次校园招聘活动反应热烈，企业的摊位前人头攒动，近万名毕业生进场应聘。毕业生们表示，东莞是制造业名城，就业机会较多，个人发展空间较大，对他们很有吸引力，本次招聘会提供的岗位与学校的专业对口，适合毕业生应聘。据现场统计，共有1700多名高校毕业生与企业达成初步就业意向。招聘会后，市人力资源局与长江大学工程技术学院签订了校政合作框架协议，并且组织企业与荆州职业技术学院、湖北中医药高等专科学校等多个院校进行交流对接，推动东莞企业与湖北优秀大专院校开展长期合作。 </p>'),
(75, 1, '“校企合作江西行”校园招聘活动圆满举办', 3, 1, 0, 0, 1412583116, 0, NULL, 31, NULL, 1, '<p> 为帮助企业开发人力资源，推进校企合作工作开展，10月23-25日，由市人力资源局主办、市职业介绍服务中心承办的“校企合作江西行”校园招聘活动在江西圆满举办。 本次活动，市职业介绍服务中心组织了50家有用工需求规划的优质企业赴江西省，分别在江西科技学院、南昌航空大学、华东交通大学及华东交通大学理工学院举办四场“东莞名企专场校园招聘会”，其中华东交通大学理工学院专场为我市首次举办的夜场校园招聘会。 参加本次招聘活动的企业有东莞深赤湾港务有限公司、东莞万士达液晶显示器有限公司、广东生益科技股份有限公司等50家优质企业，提供了各类工程师、研发技术员、模具设计人员、翻译、客户经理、财务人员及文员等6500多个需求岗位。 四场校园招聘会共吸引了2万多名毕业生进场应聘，应聘毕业生们表示此次参加招聘的东莞企业在薪资福利待遇方面非常具有吸引力，且岗位需求方面也基本涵盖了学校所有专业。据现场统计，共有5216名毕业生与企业达成初步就业意向，目前已有部分学生到岗工作。 此次校园招聘活动不仅成功帮助东莞企业吸收新生劳动力，也促成企业与江西学校的中长期开发合作。 </p>'),
(76, 1, '关于印发《东莞市人力资源局异地务工人员失业登记管理试行办法》的通知', 4, 1, 0, 0, 1412583117, 0, NULL, 31, NULL, 1, '<p> 东莞市人力资源局异地务工人员失业登记管理试行办法 第一章 总 则 第一条 为规范本市异地务工人员的失业登记管理，根据《中华人民共和国就业促进法》、《广东省实施〈中华人民共和国就业促进法〉办法》，以及《就业服务与就业管理规定》、《广东省劳动和社会保障厅关于就业和失业登记管理的试行办法》（粤劳社〔2006〕125号）等规定，结合本市实际，制定本试行办法。 第二条 本办法所称的异地务工人员是指在东莞市行政区域内就业、创业的外省市户籍劳动力，不包括台湾、香港、澳门居民和外国人。 第三条 东莞市行政区域内异地务工人员的失业登记管理，适用本办法。 第四条 市人力资源局负责全市异地务工人员失业登记工作的管理和监督。 各镇（街）人力资源分局、各村（社区）人力资源服务站具体负责辖区内异地务工人员的失业登记工作。 市职业介绍服务中心、各镇（街）人力资源分局、各村（社区）人力资源服务站（以下统称就业服务机构）具体负责辖区内登记失业异地务工人员的就业服务工作。 第五条 市人力资源局负责建立健全失业登记管理系统，对全市异地务工人员的失业登记信息实行信息化、动态化、规范化管理。 第二章 失业登记条件及程序 第六条 异地务工人员办理失业登记，应当同时符合以下条件： （一）在法定劳动年龄内、有劳动能力和就业要求； （二）已在本市办理就业登记； （三）在本市连续就业满6个月后失业，或者在本市开办个体工商户、私营企业或民办非企业单位满6个月后停业。 第七条 异地务工人员办理失业登记的，应由本人自符合失业登记条件之日起90日内持以下有效资料到就业、创业所在地的村（社区）人力资源服务站或镇（街）人力资源分局提出办理失业登记的申请： （一）连续就业满6个月后失业的： 1．本人身份证原件和复印件； 2．《东莞市异地务工人员失业登记表》（点击下载）； 3．与原用人单位签订的劳动合同原件和复印件，或失业前6个月以上（含6个月）的社保缴费清单； 4．原用人单位出具的解除或者终止劳动关系证明，或有关部门出具的能够证明与原用人单位解除或者终止劳动关系的生效法律文书（判决书或裁决书）。 （二）开办个体工商户、私营企业或民办非企业单位满6个月后停业的： 1．本人身份证原件和复印件； 2．《东莞市异地务工人员失业登记表》（点击下载）； 3．工商部门出具的注销证明，或民政部门出具的注销证明及民办非企业单位登记证书复印件。 第八条 村（社区）人力资源服务站或镇（街）人力资源分局应当自受理失业登记申请之日起5个工作日内对申请人的有关情况和资料进行审核。 符合失业登记条件的，给予办理失业登记，将登记失业人员的个人资料录入失业登记管理系统，并发放《就业失业登记证（仅限本市使用）》，同时建立个人资料档案。 第三章 公共就业服务 第九条 登记失业的异地务工人员可凭身份证或社会保障卡，在本市按规定享受以下服务： （一）免费政策咨询、职业介绍、职业指导服务； （二）按国家和地方政府相关规定享受职业技能培训资助； （三）按国家和地方政府相关规定享受扶持政策； （四）符合领取失业保险待遇条件的，可申领失业保险待遇。 第十条 就业服务机构应积极为登记失业的异地务工人员提供以下就业服务： （一）在每月10日（遇节假日顺延至下一个工作日）的“就业服务日”中提供政策咨询、职业指导、岗位推荐等服务。 （二）每个月（30天）提供至少1次的就业服务。 第十一条 就业服务机构应在失业登记管理系统中做好每位登记失业人员的跟踪服务记录，并凭服务记录情况审验办理登记失业人员申请延期等相关手续。 第四章 登记失业异地务工人员的管理 第十二条 对异地务工人员的登记失业实行有效期限管理，每次有效期限为1个月（30天）。 登记失业异地务工人员在有效期限内应当至少1次到就业服务机构接受就业服务。如仍未实现就业或自主创业的，可以申请延期。 第十三条 登记失业的异地务工人员办理申请延期的，应由本人持《就业失业登记证（仅限本市使用）》到原来办理失业登记的村（社区）人力资源服务站或镇（街）人力资源分局办理延期。 申请延期的期限为有效期限届满后的10个工作日内。 第十四条 登记失业的异地务工人员有下列情形之一，由就业服务机构作退出失业登记处理，失业登记记录失效： （一）通过各种形式实现就业或自主创业的； （二）已享受基本养老保险待遇的； （三）超过法定退休年龄的； （四）在法定劳动年龄内，但已完全丧失劳动能力的； （五）已入学、应征服兵役、移居市外或境外、被判刑收监执行的； （六）终止就业要求或拒绝接受公共就业服务的； （七）在登记失业有效期限内没有到就业服务机构接受就业服务； （八）经审核确认处于非失业状态的其他情形。 第五章 《就业失业登记证》的发放和管理 第十五条 由人力资源和社会保障部监制的《就业失业登记证》是在全国范围内有效的凭证，是记载劳动者就业和失业状况、享受相关就业扶持政策、接受公共就业服务等情况的基本载体，是劳动者按规定享受相关就业扶持政策和接受公共就业服务的有效凭证。 第十六条 由东莞市人力资源局印制的《就业失业登记证（仅限本市使用）》，是我局在对登记失业异地务工人员实现信息化管理的基础上，印制的简易版《就业失业登记证》，仅用于记载劳动者基本信息、失业登记信息和税收优惠标识，仅限于本市范围内使用。 登记失业异地务工人员若需跨市、跨省享受服务，可到最近一次办理失业登记的镇（街）人力资源分局换领国家通用的《就业失业登记证》。 第十七条 登记失业异地务工人员的基本信息发生变更的，需及时到原来办理失业登记的就业服务机构办理基本信息变更手续。由于不及时变更所产生的不利后果，由当事人自己承担。 《就业失业登记证》或者《就业失业登记证（仅限本市使用）》遗失或损毁的，应及时向发证机构报失和申请补发。 《就业失业登记证》或者《就业失业登记证（仅限本市使用）》不得转借、转让、涂改，否则视作无效。 第六章 附则 第十八条 本办法由市人力资源局负责解释。 第十九条 本办法自印发之日起实施，有效期至2015年12月31日。 </p>'),
(77, 1, '关于调整就业困难人员工资差额补助、灵活就业补助、大中专毕业生企业就业岗位津贴三项政策申领操作的通告', 4, 1, 0, 0, 1412583117, 0, NULL, 31, NULL, 1, '<p> 为完善公共就业补贴申领制度，从2013年9月1日起，调整就业困难人员工资差额补助、就业困难人员灵活就业补助、大中专毕业生企业就业岗位津贴三项政策的申领操作，具体调整内容通告如下： 一、就业困难人员工资差额补助、灵活就业补助： 申领时限为4个月内，申请人应当在4个月内提交补助申请，逾期不受理。（例如：申领2013年5月份的就业补助，必须在2013年9月底前到村或社区人力资源服务站提交申请资料。） 符合申领条件的人员，其2013年5月份以前的就业补助申请，也请务必于2013年9月30日前提交，逾期不受理。 二、大中专毕业生企业就业岗位津贴： （一）申领地调整为户籍所在地，由申请人本人到户籍所在镇（街）人力资源分局提交津贴申领资料。 （二）申领时限调整为毕业三年后的3个月内，申请人应当在毕业三年后的3个月内到人力资源分局完成提交津贴申请，逾期不受理。（例如：申领人2010年7月1日毕业，必须在2013年9月30日前完成提交申领。） 对2008年至2010年6月30日期间毕业且符合申领条件的人员，也请务必于2013年9月30日前提交津贴申请，逾期不受理。 三、实施就业实地核查及违规处罚： 对于初次申领补贴的，申请人提交申请资料后，将对每位申请人的就业情况进行工作单位实地核查，经实地核查就业（收入）等相关情况符合申领补助要求的，才同意申领补助（津贴）。 对于2013年5月份前已申领补贴并延续申领的，也将进行实地核查，如发现申领人有弄虚作假行为的，将视情况作出责令退款，以及不得再申领该项就业补助的处罚。 2013年5月29日 </p>'),
(78, 1, '关于自主参训补贴政策中属于国家职业资格证书补贴申请的补充通知', 4, 1, 0, 0, 1412583117, 0, NULL, 31, NULL, 1, '<p> 各人力资源分局： 为加强就业专项资金的使用管理，确保专款专用，对自主参训补贴政策中属于国家职业资格证书类的补贴申请，在严格执行《东莞市人力资源局自主参训补贴办法》（东人发〔2011〕17号）规定的基础上，现就相关操作细则进一步明确如下： &nbsp; &nbsp; 一、申请人所持国家职业资格证书，必须通过国家人力资源和社会保障部官网（http://www.osta.org.cn/）或广东省人力资源和社会保障厅官网 （http://www.gdhrss.gov.cn/）验证。对可在其他行业官网上通过验证，但在国家人力资源和社会保障部官网或广东省人力资源和社会保障厅官网未能通过验证的，不予受理。 &nbsp; &nbsp; 二、申请人除提交东人发〔2011〕17号文规定资料外，还需提供与职业资格证书级别相对应的符合申报职业技能鉴定条件的佐证材料（具体佐证材料见附件），佐证材料需存入申请人档案内。对于目前各人力资源分局已收取申请材料、但未通过市审核的，亦需补交佐证材料方可通过审核，请各分局向申请人做好解释工作。 &nbsp; &nbsp; 三、本通知自2014年1月1日起实施。原《关于自主参训补贴中属于国家职业资格证书补贴申请的补充通知》（东人函〔2012〕307号）同时废止。 附件：申请国家职业资格证书自主参训补贴提供佐证材料目录 东莞市人力资源局 2013年12月27日 </p>'),
(79, 1, '2014年9月21日南城区秋季招聘会通知', 4, 1, 0, 0, 1412583117, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;为了给企业提供更好、更高效的人才服务，为求职者提供更多、更优质的工作机会，南城人力资源分局、南城科技办、东莞天安数码城联合举办南城区2014年秋季现场招聘会。 时间：2014年9月21日（星期日）13:00-17:00，地点：南城区黄金路1号东莞天安数码城A1栋南城科技服务中心。 &nbsp; &nbsp;届时共有100多家企业参与招聘，提供工程师、电子技术、法律、销售、行政等1500多个岗位。热烈欢迎广大应、历届毕业生和求职人员踊跃参加！ &nbsp; 咨询电话：33333999-3910（东莞天安数码城） 　　　　　22901677（东莞市人力资源局南城分局） &nbsp;公交车路线： 【12 路】 樟村——东莞天安数码城西——南城汽车站 【X11路】 第一国际东——南城步行街——东莞天安数码城西 东莞市人力资源局南城分局 2014年9月12日 </p>'),
(80, 1, '7月10日市职介中心现场招聘会简讯', 4, 1, 0, 0, 1412583118, 0, NULL, 31, NULL, 1, '<p> 7月10日上午，市职业介绍服务中心在市就业服务大厅举办了“就业服务日”现场招聘会活动，万士达液晶显示器有限公司、广东国泰国际旅行社有限公司、中镓半导体科技有限公司、远梦家居用品股份有限公司、光明中学等25家企业入场设摊，现场进行面试招聘。&nbsp; </p> <p> &nbsp; &nbsp; 招聘会提供了工程师、财务人员、策划、翻译、厨师、保安等各类岗位近340个，吸引200多人次进场求职，其中约78名求职者与企业达成初步就业意向。 当日全市各镇街分别以现场招聘会、集中岗位推荐、职业指导讲座、就业政策宣传等方式开展“就业服务日”活动，共提供9645个招聘岗位。 </p>'),
(81, 1, '岗位津贴公示 2014-09期', 4, 1, 0, 0, 1412583118, 0, NULL, 31, NULL, 1, '<p> 根据《东莞生源2008年及以后毕业的大中专毕业生企业就业岗位津贴申领办法》，对2008年以后（含2008年）毕业的东莞户籍（指入学前户籍已在东莞并毕业后回莞就业的）大中专毕业生毕业后，在东莞市内企业、个体经济组织、民办非企业单位、依法成立的会计师事务所和律师事务所等组织就业的，给予“企业就业岗位津贴” &nbsp; 补贴标准为：中专200元/月，大专400元/月，本科500元/月，硕士以上800元/月。 现将　2014　　年　09　月审核通过的大中专毕业生企业就业岗位津贴情况公示如下：（见附表） 投诉电话：22251176 公示时间：　　10　月　08　日 至　10　　月　09 日 东莞市就业管理办公室 　2014 年　10　月　09　日　 </p>'),
(82, 1, '关于自主参训补贴政策中属于国家职业资格证书补贴申请的补充通知', 4, 1, 0, 0, 1412583118, 0, NULL, 31, NULL, 1, '<p> 各人力资源分局： 为加强就业专项资金的使用管理，确保专款专用，对自主参训补贴政策中属于国家职业资格证书类的补贴申请，在严格执行《东莞市人力资源局自主参训补贴办法》（东人发〔2011〕17号）规定的基础上，现就相关操作细则进一步明确如下： &nbsp; &nbsp; 一、申请人所持国家职业资格证书，必须通过国家人力资源和社会保障部官网（http://www.osta.org.cn/）或广东省人力资源和社会保障厅官网 （http://www.gdhrss.gov.cn/）验证。对可在其他行业官网上通过验证，但在国家人力资源和社会保障部官网或广东省人力资源和社会保障厅官网未能通过验证的，不予受理。 &nbsp; &nbsp; 二、申请人除提交东人发〔2011〕17号文规定资料外，还需提供与职业资格证书级别相对应的符合申报职业技能鉴定条件的佐证材料（具体佐证材料见附件），佐证材料需存入申请人档案内。对于目前各人力资源分局已收取申请材料、但未通过市审核的，亦需补交佐证材料方可通过审核，请各分局向申请人做好解释工作。 &nbsp; &nbsp; 三、本通知自2014年1月1日起实施。原《关于自主参训补贴中属于国家职业资格证书补贴申请的补充通知》（东人函〔2012〕307号）同时废止。 附件：申请国家职业资格证书自主参训补贴提供佐证材料目录 </p>'),
(83, 1, '2014年3月7日中堂镇“春风行动”现场招聘会消息', 4, 1, 0, 0, 1412583118, 0, NULL, 31, NULL, 1, '<p> 根据《东莞生源2008年及以后毕业的大中专毕业生企业就业岗位津贴申领办法》， &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 对2008年以后（含2008年）毕业的东莞户籍（指入学前户籍已在东莞并毕业后回莞就业的）大中专毕业生毕业后，在东莞市内企业、个体经济组织、民办非企业单位、依法成立的会计师事务所和律师事务所等组织就业的，给予“企业就业岗位津贴” 补贴标准为：中专200元/月，大专400元/月，本科500元/月，硕士以上800元/月。 &nbsp; &nbsp; &nbsp; &nbsp; 现将　2014　　年　09　月审核通过的大中专毕业生企业就业岗位津贴情况公示如下：（见附表） 投诉电话：22251176 公示时间：　　10　月　08　日 至　10　　月　09 日 </p>'),
(84, 1, '2014年2月22日虎门镇“春风行动”现场招聘会通知', 4, 1, 0, 0, 1412583119, 0, NULL, 31, NULL, 1, '<p> &nbsp; 为了搭建用工单位和求职者供需对接平台，促进本地居民和异地务工人员就业，解决企业用工需求，虎门人力资源分局定于2014年2月22日举办虎门镇春风行动现场招聘会。具体活动内容通知如下： &nbsp; 一、活动时间：2014年2月22日（星期六）上午9:00-12:00 &nbsp; 二、活动地点：虎门国际购物广场（即中心汽车站对面） &nbsp; 三、服务对象：本地失业人员和异地务工人员以及有用工需求的企业 &nbsp; &nbsp;四、活动内容：用人单位现场招聘；就业服务机构现场免费提供求职登记、岗位信息、职业介绍、职业指导和政策咨询等就业服务。 欢迎广大求职者踊跃参加！ 咨询电话：85106843 82883038 联 系 人：朱先生、吴先生 </p>'),
(85, 1, '关于茶山镇2014年免费现场招聘会时间变更的通知', 4, 1, 0, 0, 1412583119, 0, NULL, 31, NULL, 1, '<p> &nbsp; 为更好地促进异地务工人员、户籍大中专毕业生及村民就业，缓解企业招工难的问题，中堂人力资源分局定于3月7日在镇中心广场举办今年第二场“春风行动”免费现场招聘会。 &nbsp; &nbsp;届时，我镇50多家企业将提供多种岗位招聘，我分局工作人员将免费提供求职登记、职业指导、政策咨询等各项就业服务。欢迎广大求职者踊跃参加！ &nbsp; 时间：3月7日（星期五）上午9点 &nbsp; 地点：中堂镇中心广场 &nbsp; 咨询电话：88815945 </p>'),
(86, 1, '2014年2月18日黄江镇免费现场招聘会通知', 4, 1, 0, 0, 1412583119, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;为协助企业及求职人员的招、应聘工作，东莞市人力资源局黄江分局定于2月18日在黄江镇中心广场举办免费现场招聘会。具体活动通知如下：&nbsp; </p> <p> &nbsp; 一、活动时间：2014年2月18日下午2：00-5：00。&nbsp; </p> <p> &nbsp; 二、活动地点：黄江镇中心广场。&nbsp; </p> <p> &nbsp; 三、服务对象：各用人单位及广大求职人员。 四、活动内容：用人单位现场招聘；就业服务机构现场免费提供求职登记、职业指导和政策咨询等就业服务。 欢迎各用人单位及广大求职人员踊跃参加。 联系电话：83363572 联系人：陈小姐 </p>a'),
(87, 1, '高埗分局开展就业创业政策和企业用工调查', 4, 1, 0, 0, 1412583119, 0, NULL, 31, NULL, 1, '<p> 为了解就业创业政策实施情况和企业缺工状况， &nbsp; 根据市局统一部署，我分局于8月8日至9月15日开展了我镇就业创业政策和企业用工调查工作。 &nbsp; 其中，就业创业政策调查包括“大中专毕业生企业就业岗位津贴”、“自主参训补贴办法”、“创业成功奖励试行办法”等18类调查问卷，调查对象为各类政策申领对象、分局就业工作人员以及受助对象以外的其他人员； &nbsp; 企业用工调查的对象包括企业员工和人力资源主管。我分局一方面在办事大厅组织申领对象填写调查问卷， &nbsp; 另一方面先后走访鸿爱斯、文华、菲力、卓之神、嘉荣、鑫鹏、种子等10家企业，对调查各项指标进行详细解释，指导企业组织员工完成调查。 &nbsp; 目前，分局共收回《就业创业政策调查问卷》76份，《企业用工情况“员工”调查表》400份、《企业用工情况“人力资源主管”调查表》10份和《细分行业表格》10份。 </p>'),
(88, 1, '常平开展2014年第二期青年就业见习训练', 4, 1, 0, 0, 1412583120, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;为进一步开发我市户籍人力资源，有效推广青年就业见习训练，促进青年就业，根据全市统一布置，我分局于8月20日举办2014年第二期青年就业见习训练集中招训活动，广泛动员辖区内35岁以下未就业青年报名参训。 &nbsp; &nbsp; &nbsp;本次活动共有东莞永大电子有限公司、东莞市东部科技创新服务中心和东莞市建和物业管理有限公司等7家优质用人单位参加，共提供36个见习训练岗位，涉及客户经理、IE工程师、电子商务师、行政文员、品管员、储备商贸员等8个工种， &nbsp; &nbsp;活动共吸引我镇40多名青年积极参与，共有27人报名参加青年就业见习训练活动。 </p>'),
(89, 1, '麻涌镇出台《麻涌镇促进户籍人员创业就业实施方案（修订）》', 4, 1, 0, 0, 1412583120, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp; 《麻涌镇促进户籍人员创业就业实施方案》（麻府〔2011〕20号）自2011年实施以来，我镇创业就业各项工作取得了显著成效。 &nbsp; &nbsp; 随着我镇全面部署“五区九园六纵四横”、建设美丽麻涌的任务目标，对创业就业工作提出新的要求。为配合我镇发展思路，进一步完善创业就业体系，巩固和扩大创业就业成果，强化户籍劳动力的职业技能培训， &nbsp; &nbsp;麻涌镇政府对原有方案进行修订，出台了《麻涌镇促进户籍人员创业就业实施方案（修订）》（麻府〔2014〕39号），从构建公共就业服务体系入手，健全培训、就业、创业服务保障机制，优化创业就业环境，营造全民创业就业的浓厚氛围，培育壮大创业就业主体，促进全镇创业就业工作的稳步发展。 &nbsp; &nbsp;《麻涌镇促进户籍人员创业就业实施方案（修订）》分六大点，分别为总体思路、工作目标、组织保障、政策扶持、工作要求、责任追究。根据《关于进一步促进城乡居民就业创业的实施意见》（东府〔2011〕30号）及我镇实际，主要从以下方面完善和改进了我镇促进创业就业扶持政策： 1、对原实施方案中部分操作性不强的政策进行删除。2、对原实施方案中部分政策进行沿用。3、对原实施方案中部分政策进行修改。4、新增“自主参训生活津贴”、“就业帮扶人员实现就业补助”及“本镇户籍新成长劳动力创业扶持资金”政策。 </p>'),
(90, 1, '关于举办茶山镇高校毕业生专场招聘会的通知', 4, 1, 0, 0, 1412583120, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;为全面落实好免费就业服务，大力开展劳动政策法规宣传教育，帮助我镇高校毕业生解决就业、维权等切身利益问题。东莞市人力资源局茶山分局定于 &nbsp; &nbsp;2014年9月10日举办高校毕业生专场招聘会，具体活动内容通知如下： &nbsp; &nbsp;一、活动时间：上午9：00-12：00。 &nbsp; &nbsp;二、活动地点：南社村南天百货商场门前广场。 &nbsp; &nbsp;三、服务对象：应往届高校毕业生和户籍失业人员 &nbsp; &nbsp;四、活动内容：用人单位现场招聘；就业服务机构现场免费提供求职登记、岗位信息、职业介绍、职业指导和政策咨询等就业服务。 &nbsp; &nbsp;欢迎广大求职者踊跃参加！ 咨询电话：86411840 联 系 人：叶小姐、游小姐 </p>'),
(91, 1, '2014年第二期青年就业见习训练全市集中招训活动将于8月20日开展', 4, 1, 0, 0, 1412583121, 0, NULL, 31, NULL, 1, '<p> 为进一步开发人力资源，促进广大青年实现就业，市就业管理办公室与市人才管理办公室组织实施2014年的青年就业见习训练。第二期集中招训活动将于8月20日至9月10日接受广大青年报名。 &nbsp; &nbsp;一、青年就业见习训练简介 自2008年以来，由市就业管理办公室组织实施的“青年就业见习训练”。主要向户籍35岁以下的未就业青年(含普通高校毕业生)提供一次性的资助就业见习训练。 见习训练基地单位将提供各类技术类、管理类岗位，将见习人员直接安排到相关岗位上开展训练，训练期一般为2-3个月。在见习训练期内，由见习单位为学员提供相关岗位理论知识和实际操作能力的训练。训练结束后，市就业管理办公室对完成见习训练的人员发放《青年就业见习训练结业证书》；见习单位相应岗位优先招录训练合格的学员，学员与单位达成就业意向的，签订一年以上期限的无试用期劳动合同。 根据《东莞市人力资源局青年就业见习训练操作办法》（东人发〔2011〕21号）有关规定，在参训期内政府给予见习人员每人每天15元的生活津贴。本地生源普通高校毕业生参加就业见习训练，凭“东莞生源学历确认证明”，在见习期间，可以取得由财政给予见习人员每人每天35元的生活补贴。同时，所有见习单位给见习人员提供不低于1050元/月的见习训练期补助（2013年5月起）。 &nbsp; &nbsp;二、报名方式及有关事项 （一）报名方式： 1、8月20日集中接受报名： 报名时间：20日上午9时至下午12时。 报名地点：全市32个镇（街）人力资源服务中心（地址：镇街人力资源分局内）、松山湖人力资源服务中心（地址：东莞市松山湖高新技术产业开发区礼宾路1号行政办事中心附楼，东莞市人力资源局松山湖分局内）和市职业介绍服务中心（市人力资源局综合楼二楼就业服务大厅，地址：东莞市东城堑头育兴路）均可报名，由各人力资源服务中心人员代收报名资料并及时传递给相关训练单位。报名点具体地址，详见《报名点信息》。 2、分散报名：8月21日-9月10日，有意向的人员可直接与青年就业见习训练单位、见习训练单位所在地人力资源服务中心联系报名。具体联系方式（联系人、联系电话及电子邮箱）见《2014年第二期青年见习训练单位及训练岗位名录》。 （ &nbsp; &nbsp;二）报名需提交资料： 个人身份证复印件、户口簿复印件各一份，有关学历资料复印件一份（毕业生推荐表或学历证书复印件）。 &nbsp; &nbsp;（三）有关资料索取： 《2014第二期青年见习训练单位及训练岗位名录》及报名表格可在市、镇人力资源服务中心索取，或在“东莞市就业服务网http://jyfw.dg.gov.cn/”下载。 </p>'),
(92, 1, '关于印发《东莞市人力资源局创业项目征集推广试行办法》的通知', 4, 1, 0, 0, 1412583121, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;人力资源分局： &nbsp; &nbsp;为引导和鼓励户籍劳动者自主创业，面向社会征集一批“投资少、风险低、回收快”的创业项目，建立“东莞市创业项目资源库”，免费向自主创业人员提供创业项目推广服务， &nbsp; &nbsp;根据《关于进一步促进城乡居民就业创业的实施意见》（东府〔2011〕30号），特制定《东莞市人力资源局创业项目征集推广试行办法》。现印发给你们，请认真贯彻执行。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 二O一一年三月三十一日 </p>'),
(93, 1, '关于调整创业培训课时和补贴标准的通告', 4, 1, 0, 0, 1412583121, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;根据国际劳工组织开发的《创办你的企业和改善你的企业》（简称SIYB）系统培训课程，现对“创业培训”项目的培训课时和补贴标准作出调整，具体如下： &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;单位：元 </p>'),
(94, 1, '关于停止受理创业资金小额贷款申请的通知', 4, 1, 0, 0, 1412583121, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;各人力资源分局： &nbsp; &nbsp;为进一步发挥财政资金效益，深入推进我市扶创业带就业计划，提高放贷效率，根据《关于东莞市小额创业贷款实施方案有关事项的复函》（东府办复〔2014〕18号），拟调整创业资金小额贷款政策，新的具体操作办法待市政府正式文件出台后，另行通知。在新的政策出台前，请各分局做好以下工作： &nbsp; &nbsp;一、从2014年1月13日起全市人力资源部门停止受理创业资金小额贷款申请（包括一次和二次），并于1月14日下午下班前将分局已受理的小额贷款申请人资料录入系统（系统将于1月14日下午5:30之后关闭），纸质申请资料请于1月17日报送上市局就业促进科。 &nbsp; &nbsp;二、及时向申请创业资金小额贷款（包括一次和二次）的群众做好相关解释工作。 &nbsp; &nbsp;三、贷款展期申请按照正常程序继续受理。 特此通知 东莞市人力资源局 2014年1月13日 </p>'),
(95, 1, '2013年第二期青见全市集中招训活动将于7月20日开展', 4, 1, 0, 0, 1412583122, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp; 为进一步开发我市新成长人力资源，促进青年提高就业技能、实现就业，市就业管理办公室计划于7月20日（星期六）在全市就业服务机构统一开展“2013第二期青年就业见习训练全市集中招训日活动”，接受广大青年报名。 </p> <p> &nbsp; &nbsp; 今年7月起，已将非莞籍的2013年应届普通高校毕业生纳入青年就业见习训练的参训对象。 目前已收集东莞证券、光大集团、大地通讯、金叶珠宝、众生药业、永正图书等240家莞企业的3459个适合青年的见习训练岗位，欢迎各位青年朋友踊跃报名。 &nbsp; &nbsp;&nbsp; </p> <p> &nbsp; &nbsp; （一）报名方式： </p> <p> &nbsp; &nbsp; &nbsp;1、集中现场报名： 报名时间：7月20日（星期六）9：00-12：00。 报名地点：全市32个镇（街）人力资源服务中心、松山湖人力资源服务中心以及市职业介绍服务中心均可接受现场报名。报名点具体地址，详见《报名点信息》。 &nbsp; &nbsp; 2、分散联系报名：7月22日-7月31日，有意向的人员可直接与青年见习训练单位、见习训练单位所在地人力资源服务中心联系报名。具体联系方式（电子邮箱）见《2013第二期青年见习训练单位及训练岗位名录》。&nbsp; </p> <p> &nbsp; &nbsp; （二）报名需提交资料： 个人身份证复印件、户口簿复印件各1份，有关学历资料复印件一份（毕业生推荐表或学历证书复印件）。非莞籍2013年应届普通高校毕业生参加青年就业见习训练，只须提交个人身份证复印件、学历证书复印件。 &nbsp; &nbsp;&nbsp; </p> <p> &nbsp; &nbsp; （三）有关资料索取： 《2013第二期青年见习训练单位及训练岗位名录》及报名表格可在市职业介绍服务中心和各镇（街）人力资源服务中心索取，或点击下载。 咨询电话：22697037，22697710 </p>'),
(96, 1, '关于调整青年就业见习训练 相关事项的通知', 4, 1, 0, 0, 1412583122, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;各镇（街）人力资源分局： 根据《广东省人民政府办公厅转发国务院办公厅关于做好2013年全国普通高等学校毕业生就业工作的通知》（粤府办 〔2013〕22号）和《转发人力资源社会保障部关于实施离校未就业高校毕业生就业促进计划的通知》（粤人社明电〔2013〕32号）要求，现对《东莞市人力资源局青年就业见习训练操作办法》（东人发〔2011〕21号，下称《办法》）作出调整。具体调整如下： &nbsp; &nbsp;一、 扩大参加人员范围 将非莞籍的2013年应届普通高校毕业生纳入青年就业见习训练的参训对象。 &nbsp; &nbsp;二、调整需提交资料 非莞籍2013年应届普通高校毕业生参加青年就业见习训练，报名时应提交《青年就业见习训练报名表》、《青年就业见习训练参训须知》、个人身份证复印件、学历证书复印件。其余对象仍按《办法》提交相关资料。 &nbsp; &nbsp;三、调整生活津贴的补贴申领方式 青年就业见习训练期生活津贴从按月申领调整为：训练期结束后由训练单位为见习人员一次性申领生活津贴。 &nbsp; &nbsp;四、启用新表格 从2013年7月1日起，启用新的表格。 &nbsp; &nbsp;五、以上调整自下发之日起实施，试行一年。 2013年7月12日 </p>'),
(97, 1, '关于调整《东莞生源2008年及以后毕业的大中专毕业生企业就业岗位津贴申领办法》中就业单位范围的通知', 4, 1, 0, 0, 1412583123, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;各镇（街道）人力资源分局： 经研究，现将《东莞市人力资源局东莞生源2008年及以后毕业的大中专毕业生企业就业岗位津贴申领办法》（东人发〔2011〕22号）中的就业单位范围调整为：在东莞市内的企业、个体经济组织、民办非企业单位、以及依法成立的社会团体（财政供养单位下属的社会团体除外）、会计师事务所和律师事务所等组织。即增加了“社会团体”，请各镇（街道）按调整后的范围执行。 附件：2008年及以后毕业的大中专毕业生企业就业岗位津贴的有关操作指引 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 二O一一年九月九日 附件： 2008年及以后毕业的大中专毕业生 &nbsp; &nbsp;企业就业岗位津贴的有关操作指引 “十二五”促进就业创业政策实施以来，社会各届反映应将在社会团体实现就业的相关毕业生纳入津贴范围。经研究，现将“依法成立的社会团体（财政供养单位下属的社会团体除外）”纳入津贴范围。大中专毕业生企业就业岗位津贴的就业单位范围为：在东莞市内的企业、个体经济组织、民办非企业单位、以及依法成立的社会团体（财政供养单位下属的社会团体除外）、会计师事务所和律师事务所等组织。现就有关操作指引如下： 一、加大宣传，让群众知晓。特别是对2008年及以后毕业，已在市内依法成立的社会团体就业并符合相关条件的大中专毕业生，有针对性地加强宣传，使其知晓。同时，对曾经申请被拒的人员，更应想办法使其知晓，协助其尽快办理申请。但要严格按毕业起三年内最长补贴24个月的规定执行。 &nbsp; &nbsp;二、对“民办非企业单位”、“社会团体”两种组织的范畴指引如下： &nbsp; &nbsp;（一）民办非企业： &nbsp;1、教育事业，如民办幼儿园、民办小学、中学、学校、学院、大学，民办专修（进修）学院或学校，民办培训（补习）学校或中心等； &nbsp;2、卫生事业，如民办门诊部（所）、医院、民办康复、保健、卫生、疗养院（所）等； &nbsp;3、文化事业，如民办艺术表演团体、文化馆（活动中心）、图书馆（室）、博物馆（院）、美术馆、画院、名人纪念馆、收藏馆、艺术研究院（所）等； &nbsp;4、科技事业，如民办科学研究院（所、中心），民办科技传播或普及中心、科技服务中心、技术评估所（中心）等； &nbsp;5、体育事业，如民办体育俱乐部，民办体育场、馆、院、社、学校等； &nbsp;6、劳动事业，如民办职业培训学校或中心，民办职业介绍所等； &nbsp;7、民政事业，如民办福利院、敬老院、托儿所、老年公寓，民办婚姻介绍所，民办社区服务中心（站）等； &nbsp;8、社会中介服务业，如民办评估咨询服务中心（所），民办信息咨询调查中心（所），民办人才交流中心等； &nbsp;、法律服务业。 &nbsp; &nbsp;（二）社会团体： 社会团体是指我国公民行使结社权利自愿组成，为实现会员的共同意愿，按照其章程开展活动的非营利性社会组织。根据社会团体的性质和任务，社会团体可以分为学术性、行业性、专业性、联合性和基金会五类。 &nbsp;1、学术性社会团体，可分为自然科学类、社会科学类及自然科学与社会科学的交叉科学类三种，一般以学会、研究会命名。 &nbsp;2、行业性社会团体，主要是经济性团体，又可分为农业类、工业类和商业类等，一般以行业协会命名。 &nbsp;3、专业性社会团体，一般是非经济类的，主要由专业人员组成或以专业技术、专门资金，为从事某项事业而成立的团体，多以协会命名。 &nbsp;4、联合性社会团体，主要是人群的联合体或学术性、行业性、专业性团体的联合体，一般以联合会、联谊会、促进会命名。 &nbsp;5、基金会社团，指对国内的团体或个人自愿捐赠资金进行管理的组织。 &nbsp; 三、目前在我市登记的社会团体名单： 包括“（一）纳入津贴范围的社会团体名单”和不纳入津贴范围的“（二）财政供养单位下属的社会团体名单”（具体如下表）。 如申请人在提交申请时自称在社会团体工作，但社会团体名称未列入以下两类名单的，请与劳力科刘铁梅联系，联系电话：22206607。我们将向市民政局及财政局核实该社会团体是否符合条件，并及时更新相关名单。 </p>'),
(98, 1, '关于印发《东莞市人力资源局自主参训补贴办法》的通知', 4, 1, 0, 0, 1412583123, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;一、自主参训补贴对象： &nbsp;1、本市户籍劳动力（机关事业单位的公务员、管理人员、专业技术人员、工勤人员不纳入补贴范围，普通聘员及未转为普通聘员的临时工作人员纳入补贴范围）通过自主参训或自学，于2011年及以后获得国家职业资格证书、专业技术职称证书、行业职业资格认证，通过自考、成人高考、电大开放教育或网络教育获得大专或本科学历证书，且未申请政府其他形式培训补贴的，可申请自主参训补贴。 &nbsp;2、本市户籍的高等院校及中等学校在校生，于2011年及以后获得国家职业资格证书、专业技术职称证书、行业职业资格认证，可以申请自主参训补贴。 &nbsp; &nbsp;二、自主参训补贴的证书类别： &nbsp;1、国家职业资格证书； &nbsp;2、专业技术职称证书； &nbsp;3、各行业职业资格认证； &nbsp;4、自考、成人高考、电大开放教育或网络教育的大专及本科学历证书。 &nbsp; &nbsp;三、自主参训补贴项目： 具体项目名称以市就业管理办公室公布为准。 &nbsp; &nbsp;四、自主参训补贴的标准： 自主参训实行分类、分级定额补贴。具体补贴标准由市就业管理办公室公布。 &nbsp; &nbsp;五、申领自主参训补贴需提交的材料： &nbsp;1、申请人的身份证复印件； &nbsp;2、《自主参训定额补贴申请表》； &nbsp;3、申请人获得的证书复印件； &nbsp;4、属自考、成人高考、电大开放教育或网络教育获得大专及本科学历证书，申请人还需提供学历验证证明复印件。 申请人在提交以上申请资料复印件的同时，还需提交原件资料。原件资料经核对后，即时交还给申请人。 &nbsp; &nbsp;六、申领自主参训补贴的程序： 由个人自主参加各类学习、培训，完成各类相关考试认证后，持有关证书申领定额补贴： &nbsp;1、申请人持有关资料到户籍所在镇（街道）人力资源服务中心提出申请； &nbsp;2、镇（街道）人力资源服务中心对证书进行验证，并于5个工作日内核查完毕后报市就业管理办公室审核； &nbsp;3、市就业管理办公室在5个工作日内审核完毕。经核准的，由市就业资金管理部门将补贴款拨到申请人在银行开设的个人账户。 &nbsp; &nbsp;七、根据《关于进一步促进城乡居民就业创业的实施意见》（东府〔2011〕30号），对户籍城乡劳动力（机关事业单位的公务员、管理人员、专业技术人员、工勤人员不纳入补贴范围，普通聘员及未转为普通聘员的临时工作人员纳入补贴范围）五年内提供累计最高额度为5000元的培训资助，每次培训资助的额度在定额内扣减。 八、其它事项说明 严格申领补贴的审核制度。对弄虚作假、欺骗冒领的单位或个人，除追回补贴款外，五年内不得申领自主参训补贴。 </p>'),
(99, 1, '关于印发《东莞市人力资源局就业困难人员灵活就业补助办法》的通知', 4, 1, 0, 0, 1412583123, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;一、灵活就业补助对象 通过村（社区）或其他各类机构安置在市政服务、保安服务、家政服务、外包加工及其他服务岗位实现灵活就业，或个人自主实现灵活就业并纳入村（社区）劳动服务站管理的就业困难人员（以下简称补助对象）。 就业困难人员包括：1、持“再就业优惠凭证”人员；2、“4050”（即女40周岁以上，男50周岁以上）城乡失业人员；3、享受最低生活保障的城乡失业人员；4、在法定劳动年龄内且具有劳动能力的“零就业家庭”成员。 &nbsp; &nbsp;二、灵活就业补助的条件和标准 &nbsp;1、补助对象在灵活就业期间每月累计工作时间达到50小时，且月收入低于东莞市当年月最低工资标准的，补助100元/月。 &nbsp;2、补助对象在灵活就业期间每月累计工作时间达到80小时，且月收入低于东莞市当年月最低工资标准的，补助200元/月。 &nbsp; &nbsp;三、申领灵活就业补助须提交的材料 &nbsp;1、《就业困难人员灵活就业补助申领表》； &nbsp;2、补助对象的身份证复印件。 &nbsp; &nbsp;四、申领灵活就业补助的程序 灵活就业补助按月申领并发放。 &nbsp;1、补助对象将上述所规定的材料提交到村(社区)劳动服务站，由劳动服务站核实后报镇(街道)人力资源服务中心； &nbsp;2、镇(街道)人力资源服务中心在5个工作日内核查完毕并报市就业管理办公室审核； &nbsp;3、市就业管理办公室在5个工作日内审核完毕。经核准的，由市就业资金管理部门将灵活就业补助款拨到补助对象的个人银行账户。 &nbsp; &nbsp;五、其它事项说明 &nbsp;1、就业困难人员灵活就业补助实行公示制度，每月开始的5个工作日内公示上月的补贴情况，公示时间不少于7天。 &nbsp;2、严格申领补贴的审核制度。对弄虚作假、欺骗冒领的单位或个人，除追回补贴款外，五年内不得申领就业困难人员灵活就业补助。 &nbsp; &nbsp;六、本办法自颁布之日起实施，有效期至2015年12月31日止。原《关于印发〈推荐就业补贴申领办法〉等十个积极就业政策实施办法的通知》（东劳〔2006〕20号）相关规定同时废止。 </p>'),
(100, 1,'关于印发《东莞市人力资源局青年就业见习训练操作办法》的通知', 4, 1, 0, 0, 1412583123, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;东莞市人力资源局 青年就业见习训练操作办法 &nbsp; &nbsp;第一条　青年就业见习训练对象是本市户籍35岁以下的未就业青年（以下简称见习人员）。对见习人员提供一次性的资助就业见习训练。 &nbsp; &nbsp;第二条　青年就业见习训练，是按用人单位岗位需求发布见习岗位信息，将有意向参加见习人员直接安排到相关工作岗位上开展训练，训练期一般为2-3个月。在见习训练期内，由用人单位为学员提供相关岗位理论知识和实际操作能力的训练。 本办法中的单位包括：本市范围内的企业、个体经济组织、民办非企业单位、依法成立的会计师事务所、律师事务所等组织；在事业单位登记管理机关登记注册的事业单位。 &nbsp; &nbsp;第三条　承办青年就业见习训练的用人单位必须具备以下条件： &nbsp;1、依法成立并办理事业单位登记证、工商注册和税务登记，或办理民办非企业单位登记； &nbsp;2、依法保障员工的合法权益； &nbsp;3、已建立单位内部培训制度，应有相关工种岗位训练的设施设备和培训指导人员； &nbsp;4、能提供适合见习人员的技术类、管理类岗位，并有相关工种岗位的用工需求。 </p>'),
(101, 1, ' 总     则', 5, 1, 0, 0, 1412583124, 0, NULL, 31, NULL, 1, '<p> &nbsp;为了保护劳动者的合法权益，调整劳动关系，建立和维护适应社会主义市场经济的劳动制度，促进经济发展和社会进步，根据宪法，制定本法。 &nbsp;在中华人民共和国境内的企业、个体经济组织（以下统称用人单位）和与之形成劳动关系的劳动者，适用本法。 国家机关、事业组织、社会团体和与之建立劳动合同关系的劳动者，依照本法执行。 &nbsp;劳动者享有平等就业和选择职业的权利、取得劳动报酬的权利、休息休假的权利、获得劳动安全卫生保护的权利、接受职业技能培训的权利、享受社会保险和福利的权利、提请劳动争议处理的权利以及法律规定的其他劳动权利。 劳动者应当完成劳动任务，提高职业技能，执行劳动安全卫生规程，遵守劳动纪律和职业道德。 &nbsp;用人单位应当依法建立和完善规章制度，保障劳动者享有劳动权利和履行劳动义务。 &nbsp;国家采取各种措施，促进劳动就业，发展职业教育，制定劳动标准，调节社会收入，完善社会保险，协调劳动关系，逐步提高劳动者的生活水平。 &nbsp;国家提倡劳动者参加社会义务劳动，开展劳动竞 </p>'),
(102, 1, ' 促进就业', 5, 1, 0, 0, 1412583124, 0, NULL, 31, NULL, 1, '<p> &nbsp; 国家通过促进经济和社会发展，创造就业条件，扩大就业机会。 国家鼓励企业、事业组织、社会团体在法律、行政法规规定的范围内兴办产业或者拓展经营，增加就业。 国家支持劳动者自愿组织起来就业和从事个体经营实现就业。 &nbsp; 地方各级人民政府应当采取措施，发展多种类型的职业介绍机构，提供就业服务。 &nbsp; &nbsp;劳动者就业，不因民族、种族、性别、宗教信仰不同而受歧视。 &nbsp; &nbsp;妇女享有与男子平等的就业权利。在录用职工时，除国家规定的不适合妇女的工种或者岗位外，不得以性别为由拒绝录用妇女或者提高对妇女的录用标准。 残疾人、少数民族人员、退出现役的军人的就业，法律、法规有特别规定的，从其规定。 &nbsp; &nbsp;禁止用人单位招用未满十六周岁的未成年人。 文艺、体育和特种工艺单位招用未满十六周岁的未成年人，必须依照国家有关规定，履行审批手续，并保障其接受义务教育的权利 </p>'),
(103, 1, '劳动合同和集体合同', 5, 1, 0, 0, 1412583124, 0, NULL, 31, NULL, 1, '<p> &nbsp; 劳动合同是劳动者与用人单位确立劳动关系、明确双方权利和义务的协议。 建立劳动关系应当订立劳动合同。 &nbsp; &nbsp;订立和变更劳动合同，应当遵循平等自愿、协商一致的原则，不得违反法律、行政法规的规定。 劳动合同依法订立即具有法律约束力，当事人必须履行劳动合同规定的义务。 &nbsp; &nbsp;下列劳动合同无效： （一）违反法律、行政法规的劳动合同； （二）采取欺诈、威胁等手段订立的劳动合同。 &nbsp; 无效的劳动合同，从订立的时候起，就没有法律约束力。确认劳动合同部分无效的，如果不影响其余部分的效力，其余部分仍然有效。 劳动合同的无效，由劳动争议仲裁委员会或者人民法院确认。 </p>'),
(104, 1, '工作时间和休息休假', 5, 1, 0, 0, 1412583124, 0, NULL, 31, NULL, 1, '<p> &nbsp; &nbsp;国家实行劳动者每日工作时间不超过八小时、平均每周工作时间不超过四十四小时的工时制度。 &nbsp; &nbsp;对实行计件工作的劳动者，用人单位应当根据本法第三十六条规定的工时制度合理确定其劳动定额和计件报酬标准。 &nbsp; &nbsp;用人单位应当保证劳动者每周至少休息一日。 &nbsp; &nbsp;企业因生产特点不能实行本法第三十六条、第三十八条规定的，经劳动行政部门批准，可以实行其他工作和休息办法。 &nbsp; &nbsp;用人单位在下列节日期间应当依法安排劳动者休假： （一）元旦； （二）春节； （三）国际劳动节； （四）国庆节； （五）法律、法规规定的其他休假节日。 &nbsp; &nbsp; 用人单位由于生产经营需要，经与工会和劳动者协商后可以延长工作时间，一般每日不得超过一小时；因特殊原因需要延长工作时间的，在保障劳动者身体健康的条件下延长工作时间每日不得超过三小时，但是每月不得超过三十六小时。 &nbsp;有下列情形之一的，延长工作时间不受本法第四十一条规定的限制： （一）发生自然灾害、事故或者因其他原因，威胁劳动者生命健康和财产安全，需要紧急处理的； （二）生产设备、交通运输线路、公共设施发生故障，影响生产和公众利益，必须及时抢修的； （三）法律、行政法规规定的其他情形。 &nbsp; &nbsp;用人单位不得违反本法规定延长劳动者的工作时间。 &nbsp;有下列情形之一的，用人单位应当按照下列标准支付高于劳动者正常工作时间工资的工资报酬： （一）安排劳动者延长工作时间的，支付不低于工资的百分之一百五十的工资报酬； （二）休息日安排劳动者工作又不能安排补休的，支付不低于工资的百分之二百的工资报酬； （三）法定休假日安排劳动者工作的，支付不低于工资的百分之三百的工资报酬。 &nbsp; &nbsp;国家实行带薪年休假制度。 劳动者连续工作一年以上的，享受带薪年休假。具体办法由国务院规定。 </p>'),
(105, 1, ' 工     资', 5, 1, 0, 0, 1412583125, 0, NULL, 31, NULL, 1, '<p> &nbsp;工资分配应当遵循按劳分配原则，实行同工同酬。 工资水平在经济发展的基础上逐步提高。国家对工资总量实行宏观调控。 &nbsp;用人单位根据本单位的生产经营特点和经济效益，依法自主确定本单位的工资分配方式和工资水平。 &nbsp;国家实行最低工资保障制度。最低工资的具体标准由省、自治区、直辖市人民政府规定，报国务院备案。 用人单位支付劳动者的工资不得低于当地最低工资标准。 确定和调整最低工资标准应当综合参考下列因素： （一）劳动者本人及平均赡养人口的最低生活费用； （二）社会平均工资水平； （三）劳动生产率； （四）就业状况； （五）地区之间经济发展水平的差异。 &nbsp;工资应当以货币形式按月支付给劳动者本人。不得克扣或者无故拖欠劳动者的工资。 &nbsp;劳动者在法定休假日和婚丧假期间以及依法参加社会活动期间，用人单位应当依法支付工资。 </p>'),
(106, 1, '劳动安全卫生', 5, 1, 0, 0, 1412583125, 0, NULL, 31, NULL, 1, '<p> &nbsp; 用人单位必须建立、健全劳动安全卫生制度，严格执行国家劳动安全卫生规程和标准，对劳动者进行劳动安全卫生教育，防止劳动过程中的事故，减少职业危害。 &nbsp; 劳动安全卫生设施必须符合国家规定的标准。 新建、改建、扩建工程的劳动安全卫生设施必须与主体工程同时设计、同时施工、同时投入生产和使用。 &nbsp;用人单位必须为劳动者提供符合国家规定的劳动安全卫生条件和必要的劳动防护用品，对从事有职业危害作业的劳动者应当定期进行健康检查。 &nbsp; 从事特种作业的劳动者必须经过专门培训并取得特种作业资格。 &nbsp;劳动者在劳动过程中必须严格遵守安全操作规程。 劳动者对用人单位管理人员违章指挥、强令冒险作业，有权拒绝执行；对危害生命安全和身体健康的行为，有权提出批评、检举和控告。 &nbsp; &nbsp;国家建立伤亡事故和职业病统计报告和处理制度。县级以上各级人民政府劳动行政部门、有关部门和用人单位应当依法对劳动者在劳动过程中发生的伤亡事故和劳动者的职业病状况，进行统计、报告和处理。 </p>'),
(107, 1, '学校办分校、校外班的规定', 5, 1, 0, 0, 1412583125, 0, NULL, 31, NULL, 1, '<p> &nbsp; 为了加强对技工学校办分校、校外班的管理，保证办学质量，培养合格人才，特制定本规定。 &nbsp;开办技工学校分校(以下简称分校)、校外班要本着为本省、本地培养“用得着，留得住”的人才，为本省、本地的经济建设服务的目的，不能以营利为目的，要做到资源共享，优势互补，互惠互利。 &nbsp;为了调动各方面的办学积极性，实行多渠道、多形式、多层次的办学形式，以较少的投入，培养较多的人才，办学条件较好的一类以上技校可办分校，条件较好的二类技校可办校外班。分校和校外班不能再办校外班。开办了分校、校外班的技校可称为总校。 </p>'),
(108, 1, '总校应具备规定', 5, 1, 0, 0, 1412583125, 0, NULL, 31, NULL, 1, '<p> 总校必须是二类以上的技校，并在以下方面有较大的优势。 1、有较好的教学管理经验和较强的教学管理组织能力，能派出有经验、教学管理能力较强的专人巡回指导检查分校、校外班的教学及管理工作。 2、有较雄厚的师资力量，能安排专业课和部分技术理论课教师到分校、校外班任课。 3、有较完备的生产实习工厂，能安排分校、校外班的学生回总校实习半年以上。 4、开设的专业有完善的教学计划和教学大纲。能为分校、校外班提供所需专业教学计划、教学大纲、教材和教参。 </p>'),
(109, 1, '分校和校外班应具备的规定', 5, 1, 0, 0, 1412583126, 0, NULL, 31, NULL, 1, '<p> &nbsp;分校和校外班必须具备下列条件： 1、有足够的专、兼职教师，保证专业开设课程的完成。 2、有适合办学的校舍和设施，保证师生有宽松、舒适、安全的工作、学习和生活环境。 3、有训练基本功的场地和设备，保证基本功训练的完成。 4、有供学生生产实习训练的挂靠企业，井与挂靠企业签订合同，或安排回总校实习，以保证完成生产实习教学计划。 5、有可靠的经济来源，靠收费办学的，每年必须有30%以上的收入用于购置生产实习设备，改善办学条件。 &nbsp; &nbsp;分校、校外班的设置布局要合理，一般应设在人才短缺或生源充足、就业困难的市县、以解决企业所需人才和青年人学、就业的困难。 &nbsp; &nbsp;技工分校应根据当地的经济建设需要来开办当地政府给予更多的支持和帮助。 </p>'),
(110, 1, '总校与分校、校外班的职责', 5, 1, 0, 0, 1412583126, 0, NULL, 31, NULL, 1, '<p> 总校要负责对分校、校外班进行业务指导，派出专人定期巡回检查，制订统一的教学计划、大纲和教材，统一理论和操作技能的考试，统一校历和学籍管理，统一招生计划和毕业生推荐就业计划。分校、校外班负责具体实施教学计划、日常的行政管理和思想政治工作。 &nbsp;分校、校外班的教学管理和教学质量问题由总校负责。 &nbsp;本着互惠互利的原则，总校可向分校、校外班收取管理费，但不得收取过多，要考虑分校、校外班的办学困难，扶持分校、校外班充实生产实习设备，改善办学条件。收取管理费的标准控制在学费的15-30%为宜，具体按下列情况收取： 1、毕业生不需总校推荐就业的收取管理费的标准为学费的 15-20%。 2、毕业生需总校负责推荐就业的收取管理费的标准为学费的 20-25%。 3、实行1+2或2+1办学模式的，收取管理费的标准可高一些，以弥补总校实习时花费的材料费，但收取的标准最高不能超过 30%。回总校学习后，则全部费用归由总校收取。 </p>'),
(111, 1, '分校和校外班的申办程序及管理', 5, 1, 0, 0, 1412583126, 0, NULL, 31, NULL, 1, '<p> &nbsp; 为确保分校、校外班的办学质量和规模，凡需要申办分校的要征得所在市劳动局同意，经主管部门审核后报省劳动厅批准；办校外班要征得所在市县劳动局同意后，由主管部门批准并报省劳动厅备案。未经批准开办的分校、校外班一律不得招生，已经开办，但未报省劳动厅审批的要补办报批手续。 实行1+2或2+1模式的班，也要征得所在的市、县劳动部门的同意。 &nbsp; &nbsp;总校对分校、校外班每年要进行全面检查、考评。省劳动厅组织人员进行抽查。 对办学条件较好，基本达到办技校条件的，可按规定手续申办技校。 对具备办学条件的，教学质量较高的分校、校外班，发给办学许可证，有办学许可证方可招生。 对基本具备办学条件，教学质量一般的分校、校外班，要限期改善办学条件，提高教学质量，到期后办学条件、教学质量仍未达到要求的，要暂停其招生。 &nbsp;对个别条件极差，又无能力改善，以营利为目的的分校、校外班要坚决撤销。 &nbsp;对不按标准收费、毕业生就业率低的分校、校外班也要暂停其招生。 &nbsp;分校、校外班每年需填报《技工分校、校外班基本情况报表》送省劳动厅职业技能开发处，以便掌握情况。 &nbsp;本规定自印发之日起施行。 </p>'),
(112, 1, '就业许可', 5, 1, 0, 0, 1412583126, 0, NULL, 31, NULL, 1, '<p> &nbsp;用人单位聘用外国人须为该外国人申请就业许可，经获准并取得《中华人民共和国外国人就业许可证书》（以下简称许可证书）后方可聘用。 　用人单位聘用外国人从事的岗位应是有特殊需要，国内暂缺适当人选，且不违反国家有关规定的岗位。 用人单位不得聘用外国人从事营业性文艺演出，但符合本规定第九条第三项规定的人员除外。 　外国人在中国就业须具备下列条件： &nbsp;（一）年满18周岁，身体健康； &nbsp;（二）具有从事其工作所必须的专业技能和相应的工作经历； &nbsp;（三）无犯罪记录； &nbsp;（四）有确定的聘用单位； &nbsp;（五）持有有效护照或能代替护照的其他国际旅行证件（以下简称代替护照的证件）。 　在中国就业的外国人应持职业签证入境（有互免签证协议的，按协议办理），入境后取得《外国人就业证》（以下简称就业证）和外国人居留证件，方可在中国境内就业。 未取得居留证件的外国人（即持F、L、C、G字签证者）、在中国留学、实习的外国人及持职业签证外国人的随行家属不得在中国就业。特殊情况，应由用人单位按本规定规定的审批程序申领许可证书，被聘用的外国人凭许可证书到公安机关改变身份，办理就业证、居留证后方可就业。 </p>'),
(113, 1, '申请与审批', 5, 1, 0, 0, 1412583127, 0, NULL, 31, NULL, 1, '<p> &nbsp; 用人单位聘用外国人，须填写《聘用外国人就业申请表》（以下简称申请表），向其与劳动行政主管部门同级的行业主管部门（以 &nbsp; 下简称行业主管部门）提出申请，并提供下列有效文件： &nbsp;（一）拟聘用的外国人履历证明； &nbsp;（二）聘用意向书； &nbsp;（三）拟聘用外国人原因的报告； &nbsp;（四）拟聘用的外国人从事该项工作的资格证明； &nbsp;（五）拟聘用的外国人健康状况证明； &nbsp;（六）法律、法规规定的其他文件。 &nbsp;行业主管部门应按照本规定第六条、第七条及有关法律、法规的规定进行审批。 </p>'),
(114, 1, '劳动管理', 5, 1, 0, 0, 1412583127, 0, NULL, 31, NULL, 1, '<p> 　 &nbsp; 用人单位与被聘用的外国人应依法订立劳动合同。劳动合同的期限最长不得超过五年。劳动合同期限届满即行终止，但按本规定第十九条的规定履行审批手续后可以续订。 &nbsp; 　被聘用的外国人与用人单位签订的劳动合同期满时，其就业证即行失效。如需续订，该用人单位应在原合同期满前三十日内，向劳动行政部门提出延长聘用时间的申请，经批准并办理就业证延期手续。 &nbsp; 　外国人被批准延长在中国就业期限或变更就业区域、单位后，应在十日内到当地公安机关办理居留证件延期或变更手续。 &nbsp; 　被聘用的外国人与用人单位的劳动合同被解除后，该用人单位应及时报告劳动、公安部门，交还该外国人的就业证和居留证件，并到公安机关办理出境手续。 &nbsp; 　用人单位支付所聘用外国人的工资不得低于当地最低工资标准。 　 &nbsp; 在中国就业的外国人的工作时间、休息休假、劳动安全卫生以及社会保险按国家有关规定执行。 　 &nbsp; 外国人在中国就业的用人单位必须与其就业证所注明的单位相一致。 外国人在发证机关规定的区域内变更用人单位但仍从事原职业的，须经原发证机关批准，并办理就业证变更手续。 外国人离开发证机关规定的区域就业或在原规定的区域内变更用人单位且从事不同职业的，须重新办理就业许可手续。 　 &nbsp; 因违反中国法律被中国公安机关取消居留资格的外国人，用人单位应解除劳动合同，劳动部门应吊销就业证。 &nbsp; 　用人单位与被聘用的外国人发生劳动争议，应按照《中华人民共和国劳动法》和《中华人民共和国企业劳动争议处理条例》处理。 </p>');
INSERT INTO `mz_document` (`id`, `uid`, `title`, `category_id`, `display`, `status`, `views`, `create_time`, `update_time`, `from`, `level`, `description`, `order_num`, `content`) VALUES
